// App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import './App.css';
import Login from './Login';
import Admin from './Admin';
import Newuser from './Newuser';
import User from './Users';
import { UserProvider, useUser } from './UserContext';

const ProtectedRoute = ({ element, roles }) => {
  const { user } = useUser();
  const isAuthenticated = user && roles.includes(user.role);

  return isAuthenticated ? (
    element
  ) : (
    <Navigate to="/login" replace />
  );
};

function App() {
  return (
    <Router>
      <UserProvider>
        <div className="App">
          <Routes>
            <Route path="/" element={<Login />} />
            <Route path="/login" element={<Login />} />
            <Route
              path="/admin"
              element={<ProtectedRoute element={<Admin />} roles={['admin']} />}
            />
            <Route
              path="/newuser"
              element={<ProtectedRoute element={<Newuser />} roles={['admin']} />}
            />
            <Route
              path="/users"
              element={<ProtectedRoute element={<User />} roles={['user', 'admin']} />}
            />
          </Routes>
        </div>
      </UserProvider>
    </Router>
  );
}

export default App;
