import React, { useEffect, useState } from 'react';
import './style/Sidebar.css';
import Logout from './Logout';

const Nav = () => {
  const [unpurchasedCount, setUnpurchasedCount] = useState(0);

  useEffect(() => {
    fetchUnpurchasedCount();
  }, []); // Adjust the dependencies based on your application logic

  const fetchUnpurchasedCount = async () => {
    try {
      // Replace 'http://localhost:5000' with the actual URL of your PHP API
      const response = await fetch('http://localhost:5000/api/cart/unpurchasedCount?userId=YOUR_USER_ID');
      const data = await response.json();
      setUnpurchasedCount(data.unpurchasedCount);
    } catch (error) {
      console.error('Error fetching unpurchased count:', error);
    }
  };

  return (
    <div>
      <div className="area"></div>
      <nav className="main-menu">
        <ul>
          <li>
            <a href="/MainPage">
              <i className="fa fa-home fa-2x"></i>
              <span className="nav-text">Main Page</span>
            </a>
          </li>
          <li className="has-subnav">
            <a href="/EventPage">
              <i className="fa fa-globe fa-2x"></i>
              <span className="nav-text">Upcoming Events</span>
            </a>
          </li>
          <li className="has-subnav">
            <a href="/pastEvents">
              <i className="fa fa-ticket fa-2x" aria-hidden="true"></i>
              <span className="nav-text">Past Events</span>
            </a>
          </li>
          <li>
            <a href="#">
              <i className="fa fa-book fa-2x"></i>
              <span className="nav-text">Reviews</span>
            </a>
          </li>
          <li>
            <a href="/orders">
              <i className="fa fa-shopping-cart fa-2x" aria-hidden="true"></i>
              <span className="nav-text">
                Cart {unpurchasedCount > 0 && <span className="badge">{unpurchasedCount}</span>}
              </span>
            </a>
          </li>
          <li>
            <a href="/newevent">
              <i className="fa fa-lock fa-2x" aria-hidden="true"></i>
              <span className="nav-text">Create New Event</span>
            </a>
          </li>
          <li>
            <a href="#">
              <i className="fa fa-info fa-2x"></i>
              <span className="nav-text">About Us</span>
            </a>
          </li>
        </ul>

        <ul className="logout">
          <li>
            <a href="#">
              <Logout />
            </a>
          </li>
        </ul>
      </nav>
    </div>
  );
};

export default Nav;
