// EventReviewsPage.jsx

import React, { useState, useEffect } from 'react';

const EventReviewsPage = () => {
  const [eventsWithRatings, setEventsWithRatings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedReview, setSelectedReview] = useState(null);

  const fetchEventsWithRatings = async () => {
    try {
      const response = await fetch('http://localhost/api/getRatedEvents.php');

      if (!response.ok) {
        console.error(`HTTP error! Status: ${response.status}`);
        throw new Error('Error fetching rated events.');
      }

      const data = await response.json();

      if (Array.isArray(data) && data.length > 0) {
        setEventsWithRatings(data);
      } else {
        console.log('No events with ratings found.');
      }

      setLoading(false);
    } catch (error) {
      console.error('Error fetching rated events:', error);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchEventsWithRatings();
  }, []);

  const handleReviewClick = (review) => {
    setSelectedReview(review);
  };

  const handleCloseReview = () => {
    setSelectedReview(null);
  };

  return (
    <div className="EventReviewPage-container">
      {loading ? (
        <p className="EventReviewPage-loading">Loading events with ratings...</p>
      ) : (
        <div className="EventReviewPage-events-container">
          {eventsWithRatings.length > 0 ? (
            <ul className="EventReviewPage-event-list">
              {eventsWithRatings.map((event, eventIndex) => (
                <li key={event.event_id} className="EventReviewPage-event-item">
                  <h3 className="EventReviewPage-event-name">{event.event_name}</h3>
                  {event.image_link && <img src={event.image_link} alt={`${event.event_name} Image`} className="EventReviewPage-event-image" />}
                  <p className="EventReviewPage-event-rating">Rating: {Number(event.average_rating).toFixed(2)}</p>
                  {event.reviews && event.reviews.length > 0 ? (
                    <ul className="EventReviewPage-event-reviews-list">
                      {event.reviews.split('|').map((review, reviewIndex) => {
                        const [username, userRating, comment] = review.split(':');
                        const currentReview = { username, userRating, comment };
                        return (
                          <li
                            key={reviewIndex}
                            className="EventReviewPage-event-review-item"
                            onClick={() => handleReviewClick(currentReview)}
                          >
                            <p className="EventReviewPage-event-review-username">User: {username}</p>
                            <p className="EventReviewPage-event-review-user-rating">Rating: {Number(userRating).toFixed(2)}</p>
                          </li>
                        );
                      })}
                    </ul>
                  ) : (
                    <p className="EventReviewPage-no-reviews">No user reviews for this event.</p>
                  )}
                </li>
              ))}
            </ul>
          ) : (
            <p className="EventReviewPage-no-events">No events with ratings found.</p>
          )}
        </div>
      )}
      {selectedReview && (
        <div className="EventReviewPage-selected-review-container" onClick={handleCloseReview}>
          <div className="EventReviewPage-selected-review-content">
            <p className="EventReviewPage-selected-review-username">User: {selectedReview.username}</p>
            <p className="EventReviewPage-selected-review-rating">Rating: {Number(selectedReview.userRating).toFixed(2)}</p>
            {selectedReview.comment && <p className="EventReviewPage-selected-review-comment">Comment: {selectedReview.comment}</p>}
          </div>
        </div>
      )}
    </div>
  );
};

export default EventReviewsPage;
