import React, { useState } from 'react';
import './style/Login.css';
import Nav from './Nav';

function Newevent() {
  const [eventData, setEventData] = useState({
    event_name: '',
    event_date: '',
    location: '',
    tickets_available: '',
    price: '',
    image_link: '',
  });

  const [error, setError] = useState(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEventData({ ...eventData, [name]: value });
    setError(null);
  };

  const validateForm = () => {
    // Basic validation checks
    if (
      !eventData.event_name ||
      !eventData.event_date ||
      !eventData.location ||
      !eventData.tickets_available ||
      !eventData.price ||
      !eventData.image_link
    ) {
      setError('Please fill in all fields.');
      return false;
    }

    // You can add more specific validations for other fields if needed

    return true;
  };

  const handleAddEvent = () => {
    if (!validateForm()) {
      return;
    }

    fetch('http://localhost:8888/api/EventApi.php', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(eventData),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(`Network response was not ok, status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        if (data.success) {
          setEventData({
            event_name: '',
            event_date: '',
            location: '',
            tickets_available: '',
            price: '',
            image_link: '',
          });
          setError(null);
          console.log('Event added successfully');
        } else {
          setError(data.message);
          console.error('Error adding event:', data.message);
        }
      })
      .catch((error) => {
        setError('An error occurred. Please try again later.');
        console.error('Error:', error.message);
      });
  };


  return (
    <div className="galvana-kaste">
      <div className="otra-kaste">
        <label className='new-event-label'>Event Name</label>
        <input
          className='new-event-input'
          name='event_name'
          value={eventData.event_name}
          onChange={handleInputChange}
        />
      </div>
      <div className="otra-kaste">
        <label className='new-event-label'>Event Date</label>
        <input
          className='new-event-input'
          name='event_date'
          type='date'
          value={eventData.event_date}
          onChange={handleInputChange}
        />
      </div>
      <div className='otra-kaste'>
        <label className='new-event-label'>Location</label>
        <input
          className='new-event-input'
          name='location'
          value={eventData.location}
          onChange={handleInputChange}
        />
      </div>
      <div className="otra-kaste">
        <label className='new-event-label'>Tickets Available</label>
        <input
          className='new-event-input'
          name='tickets_available'
          type='number'
          value={eventData.tickets_available}
          onChange={handleInputChange}
        />
      </div>
      <div className="otra-kaste">
        <label className='new-event-label'>Price</label>
        <input
          className='new-event-input'
          name='price'
          type='number'
          value={eventData.price}
          onChange={handleInputChange}
        />
      </div>
      <div className="otra-kaste">
        <label className='new-event-label'>Image Link</label>
        <input
          className='new-event-input'
          name='image_link'
          value={eventData.image_link}
          onChange={handleInputChange}
        />
        {error && (
          <div className="error-message">
            {error}
          </div>
        )}
      </div>
      <div className='new-event-button'>
        <button className='new-event-button-style' onClick={handleAddEvent}>
          Add Event
        </button>
      </div>
    </div>
  );
}

export default Newevent;
