import React, { useState } from 'react';

const EventForm = ({ addEvent }) => {
  const [formData, setFormData] = useState({
    eventName: '',
    eventDate: '',
    location: '',
    ticketsAvailable: 0,
    price: 0,
    imageLink: '',
  });

  const [isFormVisible, setIsFormVisible] = useState(false); // Add this line

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    addEvent(formData);
    setIsFormVisible(false); // Close the form after submission
  };

  return (
    <div className="event-form-container" style={{ display: isFormVisible ? 'block' : '' }}>
      <h2>Add Event</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Event Name:
          <input type="text" name="eventName" value={formData.eventName} onChange={handleChange} />
        </label>
        <label>
          Event Date:
          <input type="text" name="eventDate" value={formData.eventDate} onChange={handleChange} />
        </label>
        <label>
          Location:
          <input type="text" name="location" value={formData.location} onChange={handleChange} />
        </label>
        <label>
          Tickets Available:
          <input
            type="number"
            name="ticketsAvailable"
            value={formData.ticketsAvailable}
            onChange={handleChange}
          />
        </label>
        <label>
          Price:
          <input type="number" name="price" value={formData.price} onChange={handleChange} />
        </label>
        <label>
          Image Link:
          <input type="text" name="imageLink" value={formData.imageLink} onChange={handleChange} />
        </label>
        <button type="submit">Add Event</button>
      </form>
    </div>
  );
};

export default EventForm;
