// UserContext.js

import React, { createContext, useState, useEffect } from 'react';
import { navigate } from 'react-router-dom';

const UserContext = createContext();

const UserProvider = ({ children }) => {
  const [authStatus, setAuthStatus] = useState(false);
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    const isAuthenticated = localStorage.getItem('token') !== null;

    setAuthStatus(isAuthenticated);

    if (isAuthenticated) {
      // Fetch user data from the server and update context
      // For now, I'll just set a sample user
      // Replace this with your actual logic to fetch user data
      setUserData({
        userId: 1,
        username: 'admin',
        role: 'Admin',
      });
    } else {
      // If not authenticated, navigate to login page
      navigate('/login');
    }
  }, []);

  console.log('Auth Status:', authStatus);
  console.log('User Data:', userData);

  return (
    <UserContext.Provider value={{ authStatus, setAuthStatus, userData, setUserData }}>
      {children}
    </UserContext.Provider>
  );
};

export { UserContext, UserProvider };
