import React, { useState, useEffect, useContext } from 'react';
import { UserContext } from './UserContext';
import { useNavigate } from 'react-router-dom';
import Nav from './Nav';
import './style/OrderPage.css';

const OrdersPage = () => {
  const { authStatus, userData } = useContext(UserContext);
  const navigate = useNavigate();
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const isAuthenticated = localStorage.getItem('token') !== null;

        if (!isAuthenticated || !authStatus || !userData || !userData.userId) {
          // Redirect to login if not authenticated or userData is null or missing userId
          navigate('/login');
          return;
        }

        // Log userData for troubleshooting
        console.log('userData:', userData);

        // Fetch user's orders from the backend (replace with your API endpoint)
        const response = await fetch(`http://localhost:8888/api/getOrders.php?userId=${userData.userId}`);
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();

        // Update orders state with fetched data
        setOrders(data.orders);
        setLoading(false); // Set loading to false once data is fetched
      } catch (error) {
        console.error('Error fetching orders:', error);
        setLoading(false); // Set loading to false in case of an error
      }
    };

    fetchData();
  }, [navigate, authStatus, userData]);

  // Check if authentication is still in progress
  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <Nav />
      <div className="orders-page-main-container">
        <h2>Your Orders</h2>
        <div className="orders-container">
          {orders.map((order) => (
            <div key={order.id} className="order-item">
              <p>Order ID: {order.id}</p>
              {/* Assuming these properties are available in your order object */}
              <p>Product: {order.product}</p>
              <p>Quantity: {order.quantity}</p>
              <p>Total: ${order.total ? order.total.toFixed(2) : 0}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default OrdersPage;
