import React, { useState } from 'react';
import './style/Login.css';

const EditModal = ({ user, onSave, onCancel }) => {
    const [editedData, setEditedData] = useState({
        firstName: user.first_name,
        lastName: user.last_name,
        username: user.username,
        phone: user.phone,
        email: user.email,
        password: user.password,
    });

    const [errors, setErrors] = useState({
        firstName: '',
        lastName: '',
        username: '',
        phone: '',
        email: '',
        password: '',
    });

    const handleInputChange = (e) => {
        const { name, value, type } = e.target;

        // More robust input validation based on your requirements
        if (type === 'number' && isNaN(value)) {
            // Prevent non-numeric input for number fields
            return;
        }

        setEditedData((prevData) => ({ ...prevData, [name]: value }));
        setErrors((prevErrors) => ({ ...prevErrors, [name]: '' }));
    };

    const validateInputs = () => {
        const newErrors = {};

        // Example validations, add your own based on requirements
        if (!editedData.firstName.trim() || editedData.firstName.length > 15) {
            newErrors.firstName = 'First Name is required and must be at most 15 characters';
        }

        if (!editedData.lastName.trim() || editedData.lastName.length > 15) {
            newErrors.lastName = 'Last Name is required and must be at most 15 characters';
        }

        if (!editedData.username.trim() || editedData.username.length > 10) {
            newErrors.username = 'Username is required and must be at most 10 characters';
        }

        if (!editedData.phone.trim() || !/^\d+$/.test(editedData.phone) || editedData.phone.length > 11) {
            newErrors.phone = 'Invalid phone number (max 11 digits)';
        }

        if (!editedData.email.trim() || !/\S+@\S+\.\S+/.test(editedData.email) || editedData.email.length > 50) {
            newErrors.email = 'Invalid email address (max 50 characters)';
        }

        if (!editedData.password.trim()) {
            newErrors.password = 'Password is required';
        }

        setErrors(newErrors);

        // Return true if there are no errors, false otherwise
        return Object.keys(newErrors).length === 0;
    };


    const handleSave = async () => {
        try {
            // Validate inputs before sending the request
            if (!validateInputs()) {
                return;
            }

            const response = await fetch('http://localhost:8888/api/updateUser.php', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    userId: user.id,
                    updatedFields: editedData,
                }),
            });

            const clonedResponse = response.clone();

            console.log('Response:', response);
            console.log('Response Text:', await clonedResponse.text());

            if (response.ok) {
                const data = await response.json();

                console.log('Data:', data);

                if (data.success) {
                    onSave({
                        userId: user.id,
                        updatedFields: editedData,
                    });
                    onCancel();
                } else {
                    console.error('Error updating user data:', data.message);
                }
            } else {
                console.error('Request failed with status:', response.status);
            }
        } catch (error) {
            console.error('Error:', error);
        }
    };

    return (
        <div className='edit-modal'>
            <div className="new-user-top"> <h2 className='edit-user-h2'>Edit User Profile Details</h2></div>
            <div className="new-user-main">

            <div className="new-user-text-input">First Name</div>
                <input className='new-user-input' type='text' name='firstName' value={editedData.firstName} onChange={handleInputChange} />
                {errors.firstName && <div className="error-message">{errors.firstName}</div>}

                <div className="new-user-text-input">Last Name</div>                <input className='new-user-input' type='text' name='lastName' value={editedData.lastName} onChange={handleInputChange} />
                {errors.lastName && <div className="error-message">{errors.lastName}</div>}

            <div className="new-user-text-input">Username</div>
                <input className='new-user-input' type='text' name='username' value={editedData.username} onChange={handleInputChange} />
                {errors.username && <div className="error-message">{errors.username}</div>}


                <div className="new-user-text-input">Phone</div>
                <input className='new-user-input' type='text' name='phone' value={editedData.phone} onChange={handleInputChange} />
                {errors.phone && <div className="error-message">{errors.phone}</div>}


                <div className="new-user-text-input">Email</div>                <input className='new-user-input' type='text' name='email' value={editedData.email} onChange={handleInputChange} />
                {errors.email && <div className="error-message">{errors.email}</div>}


                <div className="new-user-text-input">Password</div>
                <input className='new-user-input'type='password' name='password' value={editedData.password} onChange={handleInputChange} />
                {errors.password && <div className="error-message">{errors.password}</div>}

            <div>
            <div className='edit-save-button'>
    <button className='click-save-button' onClick={handleSave}>
        <span className="underline-text">Save Changes</span>
    </button>
</div>
<div className='edit-save-button'>
    <button className='click-save-button' onClick={onCancel}>
        <span className="underline-text">Cancel</span>
    </button>
</div>

            </div>
            </div>
        </div>
    );
};

export default EditModal;
