import React, { useState, useEffect } from 'react';
import './style/EventPage.css';

const EventPage = () => {
  const [selectedDate, setSelectedDate] = useState('');
  const [futureDates, setFutureDates] = useState([]);

  useEffect(() => {
    // Get future dates, for example, the next 5 years and all 12 months
    const numberOfYears = 5;
    const currentYear = new Date().getFullYear();
    const futureDatesArray = [];

    for (let year = currentYear; year < currentYear + numberOfYears; year++) {
      for (let month = 0; month < 12; month++) {
        const monthName = new Date(year, month).toLocaleString('default', { month: 'long' });
        const dateString = `${year}-${month + 1 < 10 ? '0' : ''}${month + 1} ${monthName}`;
        futureDatesArray.push(dateString);
      }
    }

    setFutureDates(futureDatesArray);
  }, []);

  return (
    <div>
      <div className="event-page-main-container">
        <div className="search-container">
          <input className="search-input" type="text" placeholder="Search events" />



        </div>
      </div>
    </div>
  );
};

export default EventPage;
