import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faShoppingCart } from '@fortawesome/free-solid-svg-icons';
import Nav from './Nav';
import './style/EventPage.css';

const EventPage = ({ addToCart }) => {
  const [selectedDate, setSelectedDate] = useState('');
  const [searchInput, setSearchInput] = useState('');
  const [futureDates, setFutureDates] = useState([]);
  const [events, setEvents] = useState([]);
  const [hoveredEvent, setHoveredEvent] = useState(null);
  const [sortMode, setSortMode] = useState('asc'); // Default sorting mode is 'asc'

  useEffect(() => {
    // Replace this with your actual fetch logic
    // Fetch events from the backend
    fetch('http://localhost:8888/api/getEvents.php')
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        console.log('Fetched events:', data.events);
        // Filter events for the future
        const upcomingEvents = data.events.filter((event) => new Date(event.event_date) > new Date());
        setEvents(upcomingEvents);
      })
      .catch((error) => {
        console.error('Error fetching events:', error);
      });
  }, []);

  // Filter events based on search input
  const filteredEvents = events.filter((event) => {
    return (
      (event.event_name && event.event_name.toLowerCase().includes(searchInput.toLowerCase())) ||
      (event.location && event.location.toLowerCase().includes(searchInput.toLowerCase())) ||
      (event.genre && event.genre.toLowerCase().includes(searchInput.toLowerCase()))
    );
  });
  const getSortingModeText = (mode) => {
    switch (mode) {
      case 'asc':
        return 'Ascending';
      case 'desc':
        return 'Descending';
      case 'oldest':
        return 'Oldest';
      case 'newest':
        return 'Newest';
      case 'cheapest':
        return 'Cheapest';
      case 'closest':
        return 'Closest';
      default:
        return 'Unknown';
    }
  };

  // Sort events based on the selected sorting mode
  const sortedEvents = [...filteredEvents].sort((a, b) => {
    switch (sortMode) {
      case 'asc':
        return new Date(a.event_date) - new Date(b.event_date);
      case 'desc':
        return new Date(b.event_date) - new Date(a.event_date);
      case 'oldest':
        return a.id - b.id;
      case 'newest':
        return b.id - a.id;
      case 'cheapest':
        return a.price - b.price;
      case 'closest':
        return Math.abs(new Date(a.event_date) - new Date(selectedDate)) - Math.abs(new Date(b.event_date) - new Date(selectedDate));
      default:
        return 0;
    }
  });

  return (
      <div>
        <Nav />
        <div className="event-page-main-container">
          <div className="search-container">
            <div className="date-dropdown-container"></div>
            <div className="search-input-container">
              <input
                className="search-input"
                type="text"
                placeholder="Search events"
                value={searchInput}
                onChange={(e) => setSearchInput(e.target.value)}
              />
              <button onClick={() => setSortMode(sortMode === 'asc' ? 'desc' : 'asc')}>Toggle Sort</button>
              <p>Sorting Mode: {getSortingModeText(sortMode)}</p>
            </div>
          </div>
          <div className="event-info-second">
            {sortedEvents.map((event) => (
              <div key={event.id} className="event-info-container" onMouseEnter={() => setHoveredEvent(event.id)} onMouseLeave={() => setHoveredEvent(null)}>
                <div className="event-image-container">
                  <img className="event-image" src={event.image_link} alt="Event Image" />
                  <button className={`add-to-cart-button ${hoveredEvent === event.id ? 'hovered' : ''}`} onClick={() => addToCart(event)}>
                    <FontAwesomeIcon icon={faShoppingCart} /> Add to Cart
                  </button>
                </div>
                <div className="event-text-container">
                  <p className="event-info">Location: {event.location}</p>
                  <p className="event-info">Date: {event.event_date}</p>
                  <p className="event-info">Concert Name: {event.event_name}</p>
                  <p className="event-info">Genre: {event.genre}</p>
                  <p className="event-info">Tickets Available: {event.tickets_available}</p>
                  <p className="event-info">Price: {event.price}</p>
                </div>
              </div>
            ))}
          </div>
          <div>
            {/* Additional content goes here if needed */}
          </div>
        </div>
      </div>
    );
            }
export default EventPage;
