import React, { useState, useEffect, useContext } from 'react';
import { UserContext } from './UserContext';
import Nav from './Nav';
import './style/AverageRatingPage.css'; // Add your CSS styles if needed

const AverageRatingPage = () => {
  const { isAuthenticated, userData } = useContext(UserContext);
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await fetch(`http://localhost/api/getPastCartEvents.php?userId=${userData.userId}`, {
          method: 'GET',
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        });

        if (!response.ok) {
          console.error(`HTTP error! Status: ${response.status}`);
          throw new Error('Error fetching past cart events.');
        }

        const data = await response.json();

        if (Array.isArray(data) && data.length > 0) {
          setEvents(data);
        } else {
          console.log('No past cart events found.');
        }

        setLoading(false);
      } catch (error) {
        console.error('Error fetching past cart events:', error);
        setLoading(false);
      }
    };

    fetchEvents();
  }, [isAuthenticated, userData]);

  return (
    <div className="average-rating-page">
      <Nav />

      <div className="average-rating-page-main-container">
        <h2>Average Ratings for Past Cart Events</h2>
        <div className="average-rating-container">
          {loading ? (
            <p className="loading-message">Loading...</p>
          ) : events.length > 0 ? (
            events.map((event) => (
              <div key={event.id} className="average-rating-item-container">
                <h3>{event.event_name}</h3>
                <p>Location: {event.location}</p>
                <p>Price: ${event.price}</p>
                <p>Event Date: {event.event_date}</p>
                <img src={event.image_link} alt={event.event_name} className="event-image" />

                {event.reviews && event.reviews.length > 0 ? (
                  <div>
                    <p>Average Rating: {calculateAverageRating(event.reviews)}</p>
                    <p>Reviews:</p>
                    <ul>
                      {event.reviews.map((review) => (
                        <li key={review.id}>
                          <p>Rating: {review.rating}</p>
                          <p>Comment: {review.comment}</p>
                        </li>
                      ))}
                    </ul>
                  </div>
                ) : (
                  <p>No reviews for this event.</p>
                )}
              </div>
            ))
          ) : (
            <p className="no-events-message">No past cart events found.</p>
          )}
        </div>
      </div>
    </div>
  );
};

// Helper function to calculate the average rating
const calculateAverageRating = (reviews) => {
  const totalRating = reviews.reduce((sum, review) => sum + review.rating, 0);
  return totalRating / reviews.length;
};

export default AverageRatingPage;
