import React, { useState, useEffect } from 'react';
import './style/EventPage.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faShoppingCart } from '@fortawesome/free-solid-svg-icons';
import Nav from './Nav';
import EventForm from './EventForm';

const EventPage = ({ addToCart }) => {
  const [selectedDate, setSelectedDate] = useState('');
  const [futureDates, setFutureDates] = useState([]);
  const [isHovered, setIsHovered] = useState(false);
  const [isFormVisible, setIsFormVisible] = useState(false);
  const [eventDetails, setEventDetails] = useState({
    location: 'Concert Venue',
    date: '2024.14.05',
    name: 'Goda Vārds 5',
    performing: 'Artist(s) Mauku Sencis',
    ticketsAvailable: 10,
    price: '10$',
  });

  useEffect(() => {
    const numberOfYears = 1;
    const currentYear = new Date().getFullYear();
    const futureDatesArray = [];

    for (let year = currentYear; year < currentYear + numberOfYears; year++) {
      for (let month = 0; month < 12; month++) {
        const monthName = new Date(year, month).toLocaleString('default', { month: 'long' });
        const dateString = `${year}-${month + 1 < 10 ? '0' : ''}${month + 1} ${monthName}`;
        futureDatesArray.push(dateString);
      }
    }

    setFutureDates(futureDatesArray);
  }, []);

  const handleAddEvent = async (eventData) => {
    try {
      const response = await fetch('http://localhost:8888/api/eventApi.php', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(eventData),
      });

      const result = await response.json();

      if (result.success) {
        console.log('Event added successfully');
        // Fetch the updated list of events or perform other actions
      } else {
        console.error('Error adding event:', result.message);
      }
    } catch (error) {
      console.error('Error adding event:', error.message);
    } finally {
      setIsFormVisible(false); // Close the form after submission
    }
  };

  return (
    <div>
      <Nav />
      <div className="event-page-main-container">
        <div className="search-container">
          <div className="date-dropdown-container">
            <select
              id="dateSelect"
              value={selectedDate}
              onChange={(e) => setSelectedDate(e.target.value)}
            >
              <option value="" disabled>
                Select a date
              </option>
              {futureDates.map((date) => (
                <option key={date} value={date}>
                  {date}
                </option>
              ))}
            </select>
          </div>
          <div className="search-input-container">
            <input className="search-input" type="text" placeholder="Search events" />
            <button>SORT</button>
            {/* visible to admins only */}
            <button className='add-event' onClick={() => setIsFormVisible(true)}>ADD EVENT</button>
          </div>
        </div>
        <div className="event-info-second">
          <div
            className="event-info-container"
            onMouseEnter={() => setIsHovered(true)}
            onMouseLeave={() => setIsHovered(false)}
          >
            <div className="event-image-container">
              <img
                className="event-image"
                src="https://i.scdn.co/image/ab67616d0000b27300dfa020bf12e7b8704d2d36"
                alt="Image Description"
              />
              {isHovered && (
                <button className="add-to-cart-button" onClick={() => console.log('Added to Cart')}>
                  <FontAwesomeIcon icon={faShoppingCart} /> Add to Cart
                </button>
              )}
            </div>
            <div className="event-text-container">
              <p className="event-info">Location: {eventDetails.location}</p>
              <p className="event-info">Date: {eventDetails.date}</p>
              <p className="event-info">Concert Name: {eventDetails.name}</p>
              <p className="event-info">Performing: {eventDetails.performing}</p>
              <p className="event-info">Tickets Available: {eventDetails.ticketsAvailable}</p>
              <p className="event-info">Price: {eventDetails.price}</p>
            </div>
          </div>
        </div>
      </div>

      {isFormVisible && <EventForm addEvent={handleAddEvent} />}
    </div>
  );
};

export default EventPage;
