import React, { useState, useEffect } from 'react';
import './style/EventPage.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faShoppingCart } from '@fortawesome/free-solid-svg-icons';
import Nav from './Nav';
import EventForm from './EventForm';

const EventPage = ({ addToCart }) => {
  const [selectedDate, setSelectedDate] = useState('');
  const [futureDates, setFutureDates] = useState([]);
  const [isHovered, setIsHovered] = useState(false);
  const [isFormVisible, setIsFormVisible] = useState(false);
  const [events, setEvents] = useState([]);

  // Use useEffect to fetch dynamic events when the component mounts
  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = async () => {
    try {
      const response = await fetch('http://localhost:8888/api/eventApi.php');
      const data = await response.json();
      setEvents(data);
    } catch (error) {
      console.error('Error fetching events:', error.message);
    }
  };

  const handleAddEvent = async (eventData) => {
    try {
      const response = await fetch('http://localhost:8888/api/eventApi.php', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(eventData),
      });

      const result = await response.json();

      if (result.success) {
        console.log('Event added successfully');
        // Fetch the updated list of events after adding a new event
        fetchEvents();
      } else {
        console.error('Error adding event:', result.message);
      }
    } catch (error) {
      console.error('Error adding event:', error.message);
    } finally {
      setIsFormVisible(false); // Close the form after submission
    }
  };

  return (
    <div>
      <Nav />
      <div className="event-page-main-container">
        <div className="search-container">
          <div className="date-dropdown-container">
            <select
              id="dateSelect"
              value={selectedDate}
              onChange={(e) => setSelectedDate(e.target.value)}
            >
              <option value="" disabled>
                Select a date
              </option>
              {futureDates.map((date) => (
                <option key={date} value={date}>
                  {date}
                </option>
              ))}
            </select>
          </div>
          <div className="search-input-container">
            <input className="search-input" type="text" placeholder="Search events" />
            <button>SORT</button>
            {/* visible to admins only */}
            <button className='add-event' onClick={() => setIsFormVisible(true)}>ADD EVENT</button>
          </div>
        </div>
        {events.map((event) => (
          <div key={event.id} className="event-info-second">
            <div
              className="event-info-container"
              onMouseEnter={() => setIsHovered(true)}
              onMouseLeave={() => setIsHovered(false)}
            >
              <div className="event-image-container">
                <img className="event-image" src={event.image_link} alt="Event Image" />
                {isHovered && (
                  <button className="add-to-cart-button" onClick={() => console.log('Added to Cart')}>
                    <FontAwesomeIcon icon={faShoppingCart} /> Add to Cart
                  </button>
                )}
              </div>
              <div className="event-text-container">
                <p className="event-info">Location: {event.location}</p>
                <p className="event-info">Date: {event.event_date}</p>
                <p className="event-info">Concert Name: {event.event_name}</p>
                {/* Add more event details as needed */}
              </div>
            </div>
          </div>
        ))}
      </div>

      {isFormVisible && <EventForm addEvent={handleAddEvent} />}
    </div>
  );
};

export default EventPage;
