import React, { useState } from 'react';
import Nav from './Nav';
import './style/review.css';

const ReviewPage = () => {
  const [expandedEvents, setExpandedEvents] = useState({});
  const [currentReviewPage, setCurrentReviewPage] = useState(1);
  const [currentCommentPage, setCurrentCommentPage] = useState(1);
  const reviewsPerPage = 10; // Number of reviews to display per page
  const commentsPerPage = 3; // Number of comments to display per page

  const toggleExpand = (eventName) => {
    setExpandedEvents((prevExpanded) => ({
      ...prevExpanded,
      [eventName]: !prevExpanded[eventName] || false,
    }));
  };

  // Dummy reviews data for testing
  const reviews = [
    { eventName: 'Event 1', stars: 3 },
    { eventName: 'Event 2', stars: 4 },
    { eventName: 'Event 3', stars: 5 },
    { eventName: 'Event 4', stars: 2 },
    { eventName: 'Event 5', stars: 4 },
    { eventName: 'Event 6', stars: 5 },
    { eventName: 'Event 7', stars: 1 },
    { eventName: 'Event 8', stars: 4 },
    { eventName: 'Event 9', stars: 5 },
    { eventName: 'Event 10', stars: 3 },
    // Add more reviews as needed
  ];

  // Dummy comments data for testing
  const comments = {
    'Event 1': [
      { username: 'User1', comment: 'Comment 1' },
      { username: 'User2', comment: 'Comment 2' },
      // Add more comments as needed
    ],
    'Event 2': [
      { username: 'User3', comment: 'Comment 3' },
      { username: 'User4', comment: 'Comment 4' },
      // Add more comments as needed
    ],
    // Add more events with comments as needed
  };

  // Calculate the index range for the current review page
  const indexOfLastReview = currentReviewPage * reviewsPerPage;
  const indexOfFirstReview = indexOfLastReview - reviewsPerPage;
  const currentReviews = reviews.slice(indexOfFirstReview, indexOfLastReview);

  // Function to handle review page change
  const handleReviewPageChange = (page) => {
    setCurrentReviewPage(page);
  };

  // Function to handle comment page change
  const handleCommentPageChange = (page) => {
    setCurrentCommentPage(page);
  };

  return (
    <div className="review-container">
      <Nav />
      {currentReviews.map((review, index) => (
        <div key={index} className={`review-data ${expandedEvents[review.eventName] ? 'expanded' : ''}`} onClick={() => toggleExpand(review.eventName)}>
          <div className="review-image"></div>
          <label className='review-label'>{review.eventName}</label>
          <div className="review-stars">
            {Array.from({ length: review.stars }).map((_, starIndex) => (
              <span key={starIndex} className="star">&#9733;</span>
            ))}
          </div>
          {expandedEvents[review.eventName] && (
            <div className="comment-review">
              {/* Pagination for comments */}
              <div className="pagination">
                {Array.from({ length: Math.ceil(comments[review.eventName].length / commentsPerPage) }).map((_, index) => (
                  <button
                    key={index}
                    onClick={(e) => {
                      e.stopPropagation(); // Prevent container from closing
                      handleCommentPageChange(index + 1);
                    }}
                  >
                    {index + 1}
                  </button>
                ))}
              </div>

              {/* Display comments for the current page */}
              {comments[review.eventName].slice((currentCommentPage - 1) * commentsPerPage, currentCommentPage * commentsPerPage).map((comment, index) => (
                <div key={index} className="user-comment-review">
                  <div className="user-name">{comment.username}</div>
                  <div className="user-comment">{comment.comment}</div>
                </div>
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );
}

export default ReviewPage;
