import React, { useState, useEffect } from 'react';
import './style/EventPage.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faShoppingCart } from '@fortawesome/free-solid-svg-icons';
import Nav from './Nav';

const EventPage = ({ addToCart }) => {
  const [selectedDate, setSelectedDate] = useState('');
  const [futureDates, setFutureDates] = useState([]);
  const [events, setEvents] = useState([]);
  const [isHovered, setIsHovered] = useState(false);

  useEffect(() => {
    const numberOfYears = 1;
    const currentYear = new Date().getFullYear();
    const futureDatesArray = [];

    for (let year = currentYear; year < currentYear + numberOfYears; year++) {
      for (let month = 0; month < 12; month++) {
        const monthName = new Date(year, month).toLocaleString('default', { month: 'long' });
        const dateString = `${year}-${month + 1 < 10 ? '0' : ''}${month + 1} ${monthName}`;
        futureDatesArray.push(dateString);
      }
    }

    setFutureDates(futureDatesArray);

    // Fetch events from the backend
    fetch('http://localhost:8888/api/getEvents.php')
      .then((response) => response.json())
      .then((data) => {
        setEvents(data.events);
      })
      .catch((error) => {
        console.error('Error fetching events:', error);
      });
  }, []);

  return (
    <div>
      <Nav />
      <div className="event-page-main-container">
        <div className="search-container">
          <div className="date-dropdown-container">
            <select
              id="dateSelect"
              value={selectedDate}
              onChange={(e) => setSelectedDate(e.target.value)}
            >
              <option value="" disabled>
                Select a date
              </option>
              {futureDates.map((date) => (
                <option key={date} value={date}>
                  {date}
                </option>
              ))}
            </select>
          </div>
          <div className="search-input-container">
            <input className="search-input" type="text" placeholder="Search events" />
            <button>SORT</button>
          </div>
        </div>
        <div className="event-info-second">
          {events.map((event) => (
            <div key={event.id} className="event-info-container">
              <div
                className="event-image-container"
                onMouseEnter={() => setIsHovered(true)}
                onMouseLeave={() => setIsHovered(false)}
              >
                <img
                  className="event-image"
                  src={event.image_link}
                  alt="Event Image"
                />
                {isHovered && (
                  <button className="add-to-cart-button" onClick={() => addToCart(event)}>
                    <FontAwesomeIcon icon={faShoppingCart} /> Add to Cart
                  </button>
                )}
              </div>
              <div className="event-text-container">
                <p className="event-info">Location: {event.location}</p>
                <p className="event-info">Date: {event.event_date}</p>
                <p className="event-info">Concert Name: {event.event_name}</p>
                {/* Add other event details here */}
                <p className="event-info">Tickets Available: {event.tickets_available}</p>
                <p className="event-info">Price: {event.price}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default EventPage;
