// PastEventsPage.js

import React, { useState, useEffect, useContext } from 'react';
import { UserContext } from './UserContext';
import Nav from './Nav';
import { navigate } from 'react-router-dom'; // Import 'navigate' from 'react-router-dom'

const PastEventsPage = () => {
  const { isAuthenticated, userData } = useContext(UserContext);
  const [pastEvents, setPastEvents] = useState([]);

  useEffect(() => {
    // Fetch past events for the user
    const fetchPastEvents = async () => {
      try {
        // Add your API call to fetch past events for the user
        const response = await fetch(`http://localhost/api/getPastEvents.php?userId=${userData.userId}`);
        if (!response.ok) {
          console.error(`HTTP error! Status: ${response.status}`);
          throw new Error('Error fetching past events.');
        }
        const data = await response.json();
        setPastEvents(data);
      } catch (error) {
        console.error('Error fetching past events:', error);
      }
    };

    if (isAuthenticated && userData && userData.userId) {
      fetchPastEvents();
    }
  }, [isAuthenticated, userData]);

  return (
    <div>
      <Nav />
      <div className="past-events-container">
        <h2 className="h2teksti">Your Past Events</h2>
        <div className="past-events-list">
          {pastEvents.map((event) => (
            <div key={event.id} className="past-event-item">
              <p>Event Name: {event.event_name}</p>
              <p>Location: {event.location}</p>
              {/* Add other details you want to display */}
              <button onClick={() => navigate(`/leave-review/${event.id}`)}>Leave A Review</button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default PastEventsPage;
