import React, { useState, useEffect } from 'react';

const EventDetailsPage = () => {
  const [event, setEvent] = useState(null);
  const [averageRating, setAverageRating] = useState(0);
  const [comments, setComments] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const eventId = 123; // Replace with your actual eventId

        // Fetch event details
        const eventResponse = await fetch(`http://localhost/api/getEventDetails.php?eventId=${eventId}`, { mode: 'no-cors' });

        if (!eventResponse.ok) {
          console.error(`HTTP error! Status: ${eventResponse.status}`);
          throw new Error('Error fetching event details.');
        }
        const eventData = await eventResponse.json();
        setEvent(eventData.event);

        // Fetch average rating
        const avgRatingResponse = await fetch(`http://localhost/api/getAverageEventRating.php?eventId=${eventId}`);
        if (!avgRatingResponse.ok) {
          console.error(`HTTP error! Status: ${avgRatingResponse.status}`);
          throw new Error('Error fetching average rating.');
        }
        const avgRatingData = await avgRatingResponse.json();
        setAverageRating(avgRatingData.averageRating);

        // Fetch comments
        const commentsResponse = await fetch(`http://localhost/api/getEventComments.php?eventId=${eventId}`);
        if (!commentsResponse.ok) {
          console.error(`HTTP error! Status: ${commentsResponse.status}`);
          throw new Error('Error fetching comments.');
        }
        const commentsData = await commentsResponse.json();
        setComments(commentsData.comments);

        setLoading(false);
      } catch (error) {
        console.error('Error:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      {loading ? (
        <p>Loading...</p>
      ) : event ? (
        <div>
          <h2>{event.event_name}</h2>
          <p>Location: {event.location}</p>
          <p>Price: ${event.price}</p>
          <p>Event Date: {event.event_date}</p>
          <img src={event.image_link} alt={event.event_name} />

          <div>
            <h3>Average Rating: {averageRating}</h3>
            <h4>Comments:</h4>
            <ul>
              {comments.map((comment) => (
                <li key={comment.id}>{comment.comment}</li>
              ))}
            </ul>
          </div>
        </div>
      ) : (
        <p>Event not found.</p>
      )}
    </div>
  );
};

export default EventDetailsPage;
