// OrdersPage.js

import React, { useState, useEffect, useContext } from 'react';
import { UserContext } from './UserContext';
import { useNavigate } from 'react-router-dom';
import Nav from './Nav';
import './style/OrderPage.css';

const OrdersPage = () => {
  const { authStatus, userData } = useContext(UserContext);
  const navigate = useNavigate();
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const isAuthenticated = localStorage.getItem('token') !== null;

        if (!isAuthenticated || !userData || !userData.id) {
          // Redirect to login if not authenticated or userData is null or missing id
          navigate('/login');
          return;
        }

        // Fetch user's orders from the backend (replace with your API endpoint)
        const response = await fetch(`http://localhost:8888/api/getOrders.php?userId=${userData.id}`);
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();

        // Update orders state with fetched data
        setOrders(data.orders);
      } catch (error) {
        console.error('Error fetching orders:', error);
      }
    };

    fetchOrders();
  }, [navigate, userData]);

  return (
    <div>
      <Nav />
      <div className="orders-page-main-container">
        <h2>Your Orders</h2>
        <div className="orders-container">
          {orders.map((order) => (
            <div key={order.id} className="order-item">
              <p>Order ID: {order.id}</p>
              {/* Assuming these properties are available in your order object */}
              <p>Product: {order.product}</p>
              <p>Quantity: {order.quantity}</p>
              <p>Total: ${order.total ? order.total.toFixed(2) : 0}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default OrdersPage;
