import React, { useState, useEffect, useContext } from 'react';
import { UserContext } from './UserContext';
import { useNavigate } from 'react-router-dom';
import Nav from './Nav';
import './style/OrderPage.css';
import jsPDF from 'jspdf';
const OrdersPage = () => {
  const { isAuthenticated, userData } = useContext(UserContext);
  const navigate = useNavigate();
  const [cart, setCartState] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Check if token is available in local storage
        const token = localStorage.getItem('token');
        console.log('Token:', token);

        if (!token) {
          console.error('Token not found in local storage. Redirecting to login.');
          navigate('/login');
          return;
        }

        // Check if isAuthenticated and userData are available
        if (!isAuthenticated || !userData || !userData.userId) {
          console.error('Authentication information is missing. Redirecting to login.');
          navigate('/login');
          return;
        }

        console.log('Authenticated User:', userData);

        // Fetch user's cart items from the backend
        const response = await fetch(`http://localhost:8888/api/getCartItems.php?userId=${userData.userId}`, {
          method: 'GET',
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        });

        if (!response.ok) {
          console.error(`HTTP error! Status: ${response.status}`);
          throw new Error('Error fetching cart items.');
        }

        const data = await response.json();

        // Update cart state with fetched data
        setCartState(data.cartItems);
        setLoading(false); // Set loading to false once data is fetched
      } catch (error) {
        console.error('Error fetching cart items:', error);
        setLoading(false); // Set loading to false in case of an error
      }
    };

    fetchData();
  }, [navigate, isAuthenticated, userData]);

  const handlePurchase = async (cartItemId) => {
    try {
      // Check if token is available in local storage
      const token = localStorage.getItem('token');

      // Make a request to the backend to update the cart item status
      const response = await fetch(`http://localhost:8888/api/updateCartItemStatus.php?cartItemId=${cartItemId}`, {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        console.error(`HTTP error! Status: ${response.status}`);
        throw new Error('Error updating cart item status.');
      }

      // Update the local state to reflect the change
      const updatedCart = cart.map((item) =>
        item.id === cartItemId ? { ...item, status: 'purchased' } : item
      );

      console.log('Updated Cart:', updatedCart);

      setCartState(updatedCart);
    } catch (error) {
      console.error('Error updating cart item status:', error);
    }
  };
  const generatePDF = (eventData, purchaseDate, randomCode) => {
    const pdf = new jsPDF();
    pdf.text(`Event ID: ${eventData.id}`, 20, 20);
    pdf.text(`Purchase Date: ${purchaseDate}`, 20, 30);
    pdf.text(`Random Code: ${randomCode}`, 20, 40);

    // Save the PDF with a unique name, you can customize this
    pdf.save(`ticket_${eventData.id}_${purchaseDate}.pdf`);
  };

  return (
    <div>
    <Nav />
    <div className="orders-page-main-container">
      <h2>Your Orders</h2>
      <div className="orders-container">
        {cart.map((item) => {
          console.log('Item:', item); // Log the entire item object
          console.log('Item Status:', item.status);
          return (
            <div key={item.id} className="order-item">
              <p>Event Name: {item.event_name}</p>
              <p>Location: {item.location}</p>
              <p>Price: ${item.price}</p>
              <p>Tickets Amount: {item.tickets_amount}</p>
              {item.status && item.status !== 'purchased' && (
                <button className="purchase-button" onClick={() => handlePurchase(item.id)}>
                  Purchase
                </button>
              )}
            </div>
          );
        })}
      </div>
    </div>
  </div>
  );
};

export default OrdersPage;
