import React, { useState, useEffect, useContext, useRef } from 'react';
import { UserContext } from './UserContext';
import { useNavigate } from 'react-router-dom';
import Nav from './Nav';
import './style/OrderPage.css';
import jsPDF from 'jspdf';
import QRCode from 'qrcode';

const OrdersPage = () => {
  const { isAuthenticated, userData } = useContext(UserContext);
  const navigate = useNavigate();
  const [cart, setCart] = useState([]);
  const [loading, setLoading] = useState(true);
  const [userReviewedEvents, setUserReviewedEvents] = useState([]);
  const [unpurchasedCount, setUnpurchasedCount] = useState(0);
  const [events, setEvents] = useState([]);
  const [pastPurchases, setPastPurchases] = useState([]);
  const [showPastPurchases, setShowPastPurchases] = useState(false);
  const pdfRef = useRef();
  const [review, setReview] = useState({ rating: 0, comment: '' });
  const [alreadyReviewedMessage, setAlreadyReviewedMessage] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');

        if (!token || !isAuthenticated || !userData || !userData.userId) {
          console.error('Invalid authentication. Redirecting to login.');
          navigate('/login');
          return;
        }

        // Fetch cart items
        const responseCart = await fetch(`http://localhost/api/getCartItems.php?userId=${userData.userId}`, {
          method: 'GET',
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        });

        if (!responseCart.ok) {
          console.error(`Error fetching cart items. Status: ${responseCart.status}`);
          throw new Error('Error fetching cart items.');
        }

        const dataCart = await responseCart.json();
        const unpurchasedItems = dataCart.cartItems.filter((item) => item.status !== 'purchased');
        setUnpurchasedCount(unpurchasedItems.length);
        setCart(dataCart.cartItems);

        // Fetch past purchases
        const responsePastPurchases = await fetch(`http://localhost/api/getPastPurchases.php?userId=${userData.userId}`, {
          method: 'GET',
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        });

        if (!responsePastPurchases.ok) {
          console.error(`Error fetching past purchases. Status: ${responsePastPurchases.status}`);
          throw new Error('Error fetching past purchases.');
        }

        const dataPastPurchases = await responsePastPurchases.json();
        setPastPurchases(dataPastPurchases.pastPurchases);

        // Fetch events
        const responseEvents = await fetch('http://localhost/api/getEvents.php', {
          method: 'GET',
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        });

        if (!responseEvents.ok) {
          console.error(`Error fetching events. Status: ${responseEvents.status}`);
          throw new Error('Error fetching events.');
        }

        const dataEvents = await responseEvents.json();
        const futureEvents = dataEvents.events.filter((event) => !eventHasPassed(event.event_date));
        setEvents(futureEvents);

        setLoading(false);
      } catch (error) {
        console.error('Error fetching data:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, [navigate, isAuthenticated, userData]);

  const handlePurchase = async (cartItemId, event) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost/api/updateCartItemStatus.php?cartItemId=${cartItemId}`, {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        console.error(`Error updating cart item status. Status: ${response.status}`);
        throw new Error('Error updating cart item status.');
      }

      const updatedEvents = events.map((e) => (e.id === event.id ? { ...e, tickets_available: e.tickets_available - 1 } : e));
      setEvents(updatedEvents);

      const updatedCart = cart.map((item) =>
        item.id === cartItemId ? { ...item, status: 'purchased' } : item
      );

      setCart(updatedCart);
    } catch (error) {
      console.error('Error updating cart item status:', error);
    }
  };

  const qrCodeImageFilename = 'qrcode.png';
  const qrCodeImagePath = `${process.env.PUBLIC_URL}/images/${qrCodeImageFilename}`;

  const handleDownloadPDF = (eventData) => {
    const purchaseDate = new Date().toLocaleString();
    const randomCode = generateRandomCode();
    generatePDF(eventData, purchaseDate, randomCode);
  };

  const generatePDF = async (eventData, purchaseDate, randomCode) => {
    try {
      const pdf = new jsPDF();

      // Title
      pdf.setFont('Poppins', 'normal');
      pdf.setFontSize(20);
      pdf.setTextColor(42, 87, 141);
      pdf.text('Eventus Concerts', 20, 20);

      // User Name
      pdf.setFontSize(14);
      pdf.text(`Username: ${userData.username}`, 20, 30);

      // Concert Details
      pdf.text(`Concert Name: ${eventData.event_name}`, 20, 40);
      pdf.text(`Price: $${eventData.price}`, 20, 50);
      pdf.text(`Concert Date: ${eventData.event_date}`, 20, 60);
      pdf.text(`Location: ${eventData.location}`, 20, 70);

      // Generate QR Code
      const qrCodeDataURL = await generateQRCodeDataURL(eventData);
      const uint8Array = await convertDataURLToUint8Array(qrCodeDataURL);
      pdf.addImage(uint8Array, 'PNG', 150, 80, 40, 40);

      // Random Code
      pdf.text(`Ticket code: ${randomCode}`, 20, 130);

      pdf.save(`ticket_${eventData.id}_${purchaseDate}.pdf`);
    } catch (error) {
      console.error('Error generating PDF:', error);
    }
  };

  const generateQRCodeDataURL = async (eventData) => {
    return QRCode.toDataURL({
      type: 'image/png',
      value: `Eventus Concerts\nUsername: ${userData.username}\nConcert: ${eventData.event_name}\nPrice: $${eventData.price}\nDate: ${eventData.event_date}\nLocation: ${eventData.location}\nCode: ${generateRandomCode()}`,
    });
  };

  const convertDataURLToUint8Array = async (dataURL) => {
    const response = await fetch(dataURL);
    const blob = await response.blob();
    return new Uint8Array(await blob.arrayBuffer());
  };

  const generateRandomCode = () => {
    const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    const length = 8;
    let randomCode = '';

    for (let i = 0; i < length; i++) {
      randomCode += characters.charAt(Math.floor(Math.random() * characters.length));
    }

    return randomCode;
  };

  const handleLeaveReview = async (eventId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost/api/leaveReview.php?eventId=${eventId}`, {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(review),
      });

      if (!response.ok) {
        console.error(`Error leaving review. Status: ${response.status}`);
        throw new Error('Error leaving review.');
      }

      setUserReviewedEvents([...userReviewedEvents, eventId]);
      setAlreadyReviewedMessage('Review submitted successfully!');
    } catch (error) {
      console.error('Error leaving review:', error);
    }
  };

  const eventHasPassed = (eventDate) => {
    const now = new Date();
    const eventDateObj = new Date(eventDate);
    return now > eventDateObj;
  };

  const moveExpiredToPastPurchases = async () => {
    try {
      const token = localStorage.getItem('token');
      const expiredItems = cart.filter((item) => item.status === 'pending' && eventHasPassed(item.event_date));

      for (const item of expiredItems) {
        const response = await fetch(`http://localhost/api/updateCartItemStatus.php?cartItemId=${item.id}`, {
          method: 'POST',
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        });

        if (!response.ok) {
          console.error(`Error updating cart item status. Status: ${response.status}`);
          throw new Error('Error updating cart item status.');
        }
      }

      const updatedCart = cart.map((item) =>
        expiredItems.some((expiredItem) => expiredItem.id === item.id) ? { ...item, status: 'expired' } : item
      );

      setCart(updatedCart);
    } catch (error) {
      console.error('Error moving expired items to past purchases:', error);
    }
  };

  const togglePastPurchases = () => {
    setShowPastPurchases(!showPastPurchases);
  };

  return (
    <div>
      <Nav />
      <div className="orders-container">
        <h1>My Orders</h1>
        {loading ? (
          <p>Loading...</p>
        ) : (
          <>
            <div className="orders-buttons">
              <button onClick={() => setShowPastPurchases(false)} className={!showPastPurchases ? 'active' : ''}>
                Unpurchased ({unpurchasedCount})
              </button>
              <button onClick={() => setShowPastPurchases(true)} className={showPastPurchases ? 'active' : ''}>
                Past Purchases
              </button>
            </div>
            {showPastPurchases ? (
              <div className="past-purchases">
                <h2>Past Purchases</h2>
                {pastPurchases.length === 0 ? (
                  <p>No past purchases.</p>
                ) : (
                  pastPurchases.map((purchase) => (
                    <div key={purchase.id} className="past-purchase-item">
                      <p>
                        <strong>Event Name:</strong> {purchase.event_name}
                      </p>
                      <p>
                        <strong>Purchase Date:</strong> {purchase.purchase_date}
                      </p>
                      <p>
                        <strong>Price:</strong> ${purchase.price}
                      </p>
                    </div>
                  ))
                )}
              </div>
            ) : (
              <div className="unpurchased-items">
                <h2>Unpurchased Items</h2>
                {cart.length === 0 ? (
                  <p>No unpurchased items in your cart.</p>
                ) : (
                  cart.map((item) => (
                    <div key={item.id} className="cart-item">
                      <p>
                        <strong>Event Name:</strong> {item.event_name}
                      </p>
                      <p>
                        <strong>Event Date:</strong> {item.event_date}
                      </p>
                      <p>
                        <strong>Price:</strong> ${item.price}
                      </p>
                      <button
                        onClick={() => handlePurchase(item.id, item)}
                        disabled={eventHasPassed(item.event_date)}
                      >
                        Purchase
                      </button>
                      {eventHasPassed(item.event_date) && <p className="expired-message">Event has passed.</p>}
                      <button onClick={() => handleDownloadPDF(item)} disabled={item.status !== 'purchased'}>
                        Download PDF
                      </button>
                    </div>
                  ))
                )}
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
};

export default OrdersPage;
