// Login.js
import React, { useState, useContext } from 'react';
import { Link } from 'react-router-dom';
import { UserContext } from './UserContext';
import './style/Login.css';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const { setAuthStatus, setUserData } = useContext(UserContext);

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      // Update the API endpoint as needed
      const response = await fetch('http://localhost:8888/api/api.php', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });

      const responseData = await response.json();

      if (!response.ok || typeof responseData.role !== 'number') {
        // Invalid credentials or other error
        console.error('Error during login:', responseData.message);
        setErrorMessage(responseData.message || 'An error occurred during login');
        return;
      }

      // Successful login
      console.log('User Role:', responseData.role);

      // Update UserContext with authentication status
      setAuthStatus(true);

      // Set user data in context
      setUserData({
        userId: responseData.userId,
        username: responseData.username,
        role: responseData.role.toString(), // Convert to string if needed
      });

      // Store user information in local storage
      localStorage.setItem('userId', responseData.userId);
      localStorage.setItem('username', responseData.username);
      localStorage.setItem('role', responseData.role);

      // Redirect based on the user's role
      if (responseData.role === 1) {
        console.log('User Role is Admin');
        // Redirect to the admin page
        // You need to handle routing based on your application structure
      } else if (responseData.role === 2) {
        console.log('User Role is User');
        // Redirect to the user page
        // You need to handle routing based on your application structure
      } else {
        console.log('User Role is not Admin or User');
        // Handle other roles or redirect to a default page
        // You need to handle routing based on your application structure
      }
    } catch (error) {
      console.error('Error during login:', error);
      setErrorMessage('An error occurred during login');
    }
  };

  return (
    <div className="login-main">
      <div className="login-container">
        <div className='login-wrapper'>
          <h1>Welcome Back</h1>
          <form className='login-form' onSubmit={handleLogin}>
            <div className='input-group'>
              <label>Username</label>
              <input
                className='login-input'
                type='text'
                placeholder=''
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
            </div>
            <div className='input-group'>
              <label>Password</label>
              <input
                className='login-input'
                type='password'
                placeholder=''
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            <div className='error-message'>{errorMessage}</div>
            <div className='login-button-container'>
              <button type='submit' className='login-button'>
                Login
              </button>
            </div>
          </form>
          <p className='sign-up-link'>
            New member? <Link to="/Register">Sign Up Instead</Link>
          </p>
        </div>
      </div>
    </div>
  );
}

export default Login;
