import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faShoppingCart } from '@fortawesome/free-solid-svg-icons';
import Nav from './Nav';
import './style/EventPage.css';

const PastEvents = () => {
  const [selectedDate, setSelectedDate] = useState('');
  const [pastDates, setPastDates] = useState([]);
  const [events, setEvents] = useState([]);
  const [searchInput, setSearchInput] = useState('');
  const [hoveredEvent, setHoveredEvent] = useState(null);
  const [sortMode, setSortMode] = useState('desc'); // Default sorting mode is 'desc'
  const [currentPage, setCurrentPage] = useState(1);
  const eventsPerPage = 5;

  useEffect(() => {
    const numberOfYears = 1;
    const currentYear = new Date().getFullYear();
    const pastDatesArray = [];

    for (let year = currentYear; year > currentYear - numberOfYears; year--) {
      for (let month = 11; month >= 0; month--) {
        const monthName = new Date(year, month).toLocaleString('default', { month: 'long' });
        const dateString = `${year}-${month + 1 < 10 ? '0' : ''}${month + 1} ${monthName}`;
        pastDatesArray.push(dateString);
      }
    }

    setPastDates(pastDatesArray);

    // Fetch past events from the backend
    fetch('http://localhost:8888/api/getPastEvents.php') // Update with the correct API endpoint for past events
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        console.log('Fetched past events:', data.events);
        setEvents(data.events);
      })
      .catch((error) => {
        console.error('Error fetching past events:', error);
      });
  }, []);

  // Filter events based on search input and date
  const filteredEvents = events.filter((event) => {
    return (
      (event.event_name && event.event_name.toLowerCase().includes(searchInput.toLowerCase())) ||
      (event.location && event.location.toLowerCase().includes(searchInput.toLowerCase())) ||
      (event.genre && event.genre.toLowerCase().includes(searchInput.toLowerCase()))
    );
  });

  // Sort and paginate events
  const indexOfLastEvent = currentPage * eventsPerPage;
  const indexOfFirstEvent = indexOfLastEvent - eventsPerPage;
  const currentEvents = [...filteredEvents].sort((a, b) => {
    switch (sortMode) {
      case 'asc':
        return new Date(a.event_date) - new Date(b.event_date);
      case 'desc':
        return new Date(b.event_date) - new Date(a.event_date);
      default:
        return 0;
    }
  }).slice(indexOfFirstEvent, indexOfLastEvent);

  // Logic for displaying page numbers
  const pageNumbers = [];
  for (let i = 1; i <= Math.ceil(filteredEvents.length / eventsPerPage); i++) {
    pageNumbers.push(i);
  }

  return (
    <div>
      <Nav />
      <div className="event-page-main-container">
        <div className="search-container">
          <div className="date-dropdown-container"></div>
          <div className="search-input-container">
            <input
              className="search-input"
              type="text"
              placeholder="Search events"
              value={searchInput}
              onChange={(e) => setSearchInput(e.target.value)}
            />
            <select value={sortMode} onChange={(e) => setSortMode(e.target.value)}>
              <option value="desc">Descending</option>
              <option value="asc">Ascending</option>
            </select>
          </div>
        </div>
        <div className="event-info-second">
          {currentEvents.map((event) => (
            <div key={event.id} className="event-info-container" onMouseEnter={() => setHoveredEvent(event.id)} onMouseLeave={() => setHoveredEvent(null)}>
              <div className="event-image-container">
                <img className="event-image" src={event.image_link} alt="Event Image" />
              </div>
              <div className="event-text-container">
                <p className="event-info">Location: {event.location}</p>
                <p className="event-info">Date: {event.event_date}</p>
                <p className="event-info">Concert Name: {event.event_name}</p>
                <p className="event-info">Genre: {event.genre}</p>
                <p className="event-info">Price: {event.price}</p>
                <p className="event-info">Status: Sold</p>
              </div>
            </div>
          ))}
        </div>
        <div className="pagination">
          {pageNumbers.map((number) => (
            <span key={number} onClick={() => setCurrentPage(number)}>
              {number}
            </span>
          ))}
        </div>
      </div>
    </div>
  );
};

export default PastEvents;
