import React, { useState, useEffect } from 'react';
import './style/EventPage.css';

const EventPage = () => {
  const [selectedYear, setSelectedYear] = useState('');
  const [selectedMonth, setSelectedMonth] = useState('');
  const [futureYears, setFutureYears] = useState([]);
  const [futureMonths, setFutureMonths] = useState([]);

  useEffect(() => {
    // Get future years, for example, the next 5 years
    const numberOfYears = 5;
    const currentYear = new Date().getFullYear();
    const futureYearsArray = Array.from({ length: numberOfYears }, (_, index) => currentYear + index);
    setFutureYears(futureYearsArray);

    // Get future months
    const futureMonthsArray = Array.from({ length: 12 }, (_, index) => {
      const month = new Date(currentYear, index).toLocaleString('default', { month: 'long' });
      return { value: index + 1, label: month };
    });
    setFutureMonths(futureMonthsArray);
  }, []);

  return (
    <div>
      <div className="event-page-main-container">
        <div className="search-container">
          <input className="search-input" type="text" placeholder="Search events" />

          <div className="date-dropdown-container">
            <label htmlFor="yearSelect">Select a year:</label>
            <select
              id="yearSelect"
              value={selectedYear}
              onChange={(e) => setSelectedYear(e.target.value)}
            >
              <option value="" disabled>
                Select a year
              </option>
              {futureYears.map((year) => (
                <option key={year} value={year}>
                  {year}
                </option>
              ))}
            </select>

            <label htmlFor="monthSelect">Select a month:</label>
            <select
              id="monthSelect"
              value={selectedMonth}
              onChange={(e) => setSelectedMonth(e.target.value)}
            >
              <option value="" disabled>
                Select a month
              </option>
              {futureMonths.map((month) => (
                <option key={month.value} value={month.value}>
                  {month.label}
                </option>
              ))}
            </select>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EventPage;
