import React, { useState } from 'react';
import './style/Login.css';
import { useNavigate } from 'react-router-dom';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    if (!username || !password) {
      setError('Please enter both username and password.');
      return;
    }

    try {
      const response = await fetch('http://localhost:8888/api/api.php', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username,
          password,
        }),
      });

      // Check if the response is successful (status code in the range of 200-299)
      if (!response.ok) {
        console.error('Request failed with status:', response.status);
        setError('Invalid username or password.');
        return;
      }

      // Parse the response body as JSON
      const responseBody = await response.json();
      console.log('Response Body:', responseBody);

      if (responseBody.success !== undefined && responseBody.role !== undefined) {
        if (responseBody.success) {
          // Save user information to local storage
          localStorage.setItem('username', username);
          localStorage.setItem('password', password);
          localStorage.setItem('role', responseBody.role);

          const userRole = responseBody.role;

          // Redirect based on user role
          if (userRole === 'admin') {
            navigate('/admin');
          } else if (userRole === 'user') {
            navigate('/user');
          } else {
            navigate('/default');
          }
        } else {
          console.error('Login failed:', responseBody.message);
          setError(responseBody.message || 'Invalid username or password.');
        }
      } else {
        console.error('Invalid response structure:', responseBody);
        setError('Invalid response structure.');
      }
    } catch (error) {
      console.error('Error:', error);
      setError('An unexpected error occurred.');
    }
  };

  return (
    <div className="login-main">
      <div className="login-center">
        <div className="login-image"></div>
        <div className="loginTOP">
          <div className="login-logo"></div>
          <div className="login-title">
            <h1 className="register-h1">LOGIN</h1>
          </div>
          <form className="login-form" onSubmit={handleLogin}>
            <div className="inputFI">
              <input
                type="text"
                placeholder="Username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
            </div>
            <div className="inputFI2">
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            {error && <div className="error-message">{error}</div>}
            <div className="buttonss">
              <button type="submit" className="button1">
                LOGIN
              </button>
            </div>
            <div className="loginB"></div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Login;
