import React, { useState, useEffect, useContext } from 'react';
import { UserContext } from './UserContext';
import { useNavigate } from 'react-router-dom';
import Nav from './Nav';

const PastCartEventsPage = () => {
  const { isAuthenticated, userData } = useContext(UserContext);
  const navigate = useNavigate();
  const [pastCartEvents, setPastCartEvents] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch past events from the user's cart
        // You may need to adjust the API endpoint based on your backend
        const token = localStorage.getItem('token');
        const response = await fetch(`http://localhost/api/getPastCartEvents.php?userId=${userData.userId}`, {
          method: 'GET',
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        });

        if (!response.ok) {
          console.error(`HTTP error! Status: ${response.status}`);
          throw new Error('Error fetching past cart events.');
        }

        const data = await response.json();

        // Assuming the past cart events are present in the 'pastCartEvents' property of the API response
        if (Array.isArray(data) && data.length > 0) {
          setPastCartEvents(data); // Assuming the API returns an array directly
        } else {
          console.log('No past cart events found.');
        }

        setLoading(false);
      } catch (error) {
        console.error('Error fetching past cart events:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, [navigate, isAuthenticated, userData]);

  return (
    <div>
      {/* Include any navigation or header components as needed */}
      <div className="past-cart-events-page-main-container">
        <h2 className='h2teksti'>Your Past Cart Events</h2>
        <div className="past-cart-events-container">
          {loading ? (
            <p>Loading...</p>
          ) : pastCartEvents.length > 0 ? (
            pastCartEvents.map((event) => (
              <div key={event.id} className="past-cart-event-item-container">
                {/* Display relevant information about the past cart event */}
                <p>Event Name: {event.event.event_name}</p>
                <p>Location: {event.event.location}</p>
                <p>Price: ${event.event.price}</p>
                <p>Event Date: {event.event.event_date}</p>
                {/* Include any additional details or actions you want to provide */}
              </div>
            ))
          ) : (
            <p>No past cart events found.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default PastCartEventsPage;
