import React, { useState, useContext } from 'react';
import './style/Login.css';
import Nav from './Nav';
import { UserContext } from './UserContext';

function Newevent() {
  const { userData } = useContext(UserContext);

  // Check if user is authenticated and has admin role (role_id === 1)
  if (!userData || userData.role !== 1) {
    // If not authenticated or not an admin, you can redirect or show an error message
    return (
      <div>
        <Nav />
        <div className="galvana-kaste">
          <p>You do not have permission to access this page.</p>
        </div>
      </div>
    );
  }

  const [eventData, setEventData] = useState({
    event_name: '',
    genre: '',
    event_date: '',
    location: '',
    tickets_available: '',
    price: '',
    image_link: '',
  });

  const [errors, setErrors] = useState({
    event_name: '',
    genre: '',
    event_date: '',
    location: '',
    tickets_available: '',
    price: '',
    image_link: '',
  });

  const [successMessage, setSuccessMessage] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEventData({ ...eventData, [name]: value });
    setErrors({ ...errors, [name]: '' });
  };

  const validateForm = () => {
    const currentDate = new Date().toISOString().split('T')[0];

    const newErrors = {};

    if (!eventData.event_name) {
      newErrors.event_name = 'Event Name is required.';
    }

    if (!eventData.genre) {
      newErrors.genre = 'Genre is required.';
    }

    if (!eventData.event_date) {
      newErrors.event_date = 'Event Date is required.';
    } else if (eventData.event_date < currentDate) {
      newErrors.event_date = 'Event Date must be in the future.';
    }

    if (!eventData.location) {
      newErrors.location = 'Location is required.';
    }

    if (!eventData.tickets_available) {
      newErrors.tickets_available = 'Tickets Available is required.';
    }

    if (!eventData.price) {
      newErrors.price = 'Price is required.';
    }

    if (!eventData.image_link) {
      newErrors.image_link = 'Image Link is required.';
    }

    setErrors(newErrors);
    return Object.values(newErrors).every((error) => error === '');
  };

  const handleAddEvent = () => {
    if (!validateForm()) {
      return;
    }

    fetch('http://localhost:8888/api/EventApi.php', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        ...eventData,
        genre: eventData.genre,
      }),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(`Network response was not ok, status: ${response.status}`);
        }
        return response.text();
      })
      .then((data) => {
        setSuccessMessage('Event added successfully!');
        console.log(data);
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  };

  return (
    <div>
      <Nav />
      <div className="galvana-kaste">
        <div className="otra-kaste">
          <label className='new-event-label'>Event Name</label>
          <input
            className='new-event-input'
            name='event_name'
            value={eventData.event_name}
            onChange={handleInputChange}
          />
          {errors.event_name && (
            <div className="error-message">
              {errors.event_name}
            </div>
          )}
        </div>

        {/* (Additional form fields with validation) */}

        <div className='new-event-button'>
          <button className='new-event-button-style' onClick={handleAddEvent}>
            Add Event
          </button>
        </div>
        {successMessage && (
          <div className="success-message">
            {successMessage}
          </div>
        )}
      </div>
    </div>
  );
}

export default Newevent;
