import React, { useState, useEffect, useContext } from 'react';
import './style/EventPage.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faShoppingCart } from '@fortawesome/free-solid-svg-icons';
import Nav from './Nav';
import { CartContext } from './CartContext'; // Correct if both files are in the same directory
const EventPage = () => {
    const [selectedDate, setSelectedDate] = useState('');
    const [futureDates, setFutureDates] = useState([]);
    const [isHovered, setIsHovered] = useState(false);

    // Use CartContext
    const { cart, addToCart } = useContext(CartContext);

    useEffect(() => {
      // Get future dates, for example, the next 5 years and all 12 months
      const numberOfYears = 5;
      const currentYear = new Date().getFullYear();
      const futureDatesArray = [];

      for (let year = currentYear; year < currentYear + numberOfYears; year++) {
        for (let month = 0; month < 12; month++) {
          const monthName = new Date(year, month).toLocaleString('default', { month: 'long' });
          const dateString = `${year}-${month + 1 < 10 ? '0' : ''}${month + 1} ${monthName}`;
          futureDatesArray.push(dateString);
        }
      }

      setFutureDates(futureDatesArray);
    }, []);

    return (
      <div>
        <Nav />
        <div className="event-page-main-container">
          <div className="search-container">
            <div className="date-dropdown-container">
              <select
                id="dateSelect"
                value={selectedDate}
                onChange={(e) => setSelectedDate(e.target.value)}
              >
                <option value="" disabled>
                  Select a date
                </option>
                {futureDates.map((date) => (
                  <option key={date} value={date}>
                    {date}
                  </option>
                ))}
              </select>
            </div>
            <div className="search-input-container">
              <input className="search-input" type="text" placeholder="Search events" />
              <button>SORT</button>
            </div>
          </div>
          <div className="event-info-second">
            <div
              className="event-info-container"
              onMouseEnter={() => setIsHovered(true)}
              onMouseLeave={() => setIsHovered(false)}
            >
              <div className="event-image-container">
                <img
                  className="event-image"
                  src="https://i.scdn.co/image/ab67616d0000b27300dfa020bf12e7b8704d2d36"
                  alt="Image Description"
                />
                {isHovered && (
                  <button
                    className="add-to-cart-button"
                    onClick={() => addToCart('Event Name', 'Event ID', 'Event Price')}
                  >
                    <FontAwesomeIcon icon={faShoppingCart} /> Add to Cart
                  </button>
                )}
              </div>
              <div className="event-text-container">
                <p className="event-info">Location: Concert Venue</p>
                <p className="event-info">Date: 2024.14.05</p>
                <p className="event-info">Concert Name: Goda Vārds 5</p>
                <p className="event-info">Performing: Artist(s) Mauku Sencis</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  };

  export default EventPage;
