// OrdersPage.js

import React, { useState, useEffect, useContext } from 'react'; // Add import statement for useState and useEffect
import { UserContext } from './UserContext';
import { useNavigate } from 'react-router-dom';
import Nav from './Nav';
import './style/OrderPage.css';

const OrdersPage = () => {
  const { isAuthenticated, userData } = useContext(UserContext);
  const navigate = useNavigate();
  const [orders, setOrdersState] = useState([]); // Fix: Rename setOrders to setOrdersState
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Check if token is available in local storage
        const token = localStorage.getItem('token');
        console.log('Token:', token);

        if (!token) {
          console.error('Token not found in local storage. Redirecting to login.');
          navigate('/login');
          return;
        }

        // Check if isAuthenticated and userData are available
        if (!isAuthenticated || !userData || !userData.userId) {
          console.error('Authentication information is missing. Redirecting to login.');
          navigate('/login');
          return;
        }

        console.log('Authenticated User:', userData);

        // Fetch user's orders from the backend
        const response = await fetch(`http://localhost:8888/api/getOrders.php?userId=${userData.userId}`, {
          headers: {
            Authorization: `Bearer ${token}`, // Include the token in the Authorization header
          },
        });

        if (!response.ok) {
          console.error(`HTTP error! Status: ${response.status}`);
          throw new Error('Error fetching orders.');
        }

        const data = await response.json();

        // Update orders state with fetched data
        setOrdersState(data.orders); // Fix: Use setOrdersState to update orders state
        setLoading(false); // Set loading to false once data is fetched
      } catch (error) {
        console.error('Error fetching orders:', error);
        setLoading(false); // Set loading to false in case of an error
      }
    };

    fetchData();
  }, [navigate, isAuthenticated, userData]);

  // Check if authentication is still in progress
  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <Nav />
      <div className="orders-page-main-container">
        <h2>Your Orders</h2>
        <div className="orders-container">
          {orders.map((order) => (
            <div key={order.id} className="order-item">
              <p>Order ID: {order.id}</p>
              {/* Assuming these properties are available in your order object */}
              <p>Product: {order.product}</p>
              <p>Quantity: {order.quantity}</p>
              <p>Total: ${order.total ? order.total.toFixed(2) : 0}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default OrdersPage;
