import React, { useState, useEffect, useContext } from 'react';
import { UserContext } from './UserContext';
import { useNavigate } from 'react-router-dom';
import Nav from './Nav';

const PastEventsPage = () => {
  const { isAuthenticated, userData } = useContext(UserContext);
  const navigate = useNavigate();
  const [pastEvents, setPastEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [review, setReview] = useState({ rating: 0, comment: '' });
  const [alreadyReviewedMessage, setAlreadyReviewedMessage] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');

        if (!token) {
          console.error('Token not found in local storage. Redirecting to login.');
          navigate('/login');
          return;
        }

        if (!isAuthenticated || !userData || !userData.userId) {
          console.error('Authentication information is missing. Redirecting to login.');
          navigate('/login');
          return;
        }

        const response = await fetch(`http://localhost/api/getPastEvents.php?userId=${userData.userId}`, {
          method: 'GET',
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        });

        if (!response.ok) {
          console.error(`HTTP error! Status: ${response.status}`);
          console.error('Error details:', await response.text());
          throw new Error('Error fetching past events.');
        }

        const data = await response.json();

        console.log('API Response:', data);

        setPastEvents(data); // Assuming the API returns an array directly
        setLoading(false);
      } catch (error) {
        console.error('Error fetching past events:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, [navigate, isAuthenticated, userData]);

  const handleLeaveReview = async (eventId) => {
    // ... (same as in OrdersPage)
  };

  const eventHasPassed = (eventDate) => {
    const currentDate = new Date();
    const eventDateTime = new Date(eventDate);
    return currentDate > eventDateTime;
  };

  return (
    <div>
      <Nav />
      <div className="past-events-page-main-container">
        <h2 className="h2teksti">Your Past Events</h2>
        <div className="past-events-container">
          {loading ? (
            <p>Loading...</p>
          ) : (
            pastEvents.map((event) => (
              <div key={event.id} className="past-event-item-container">
                <p>Event Name: {event.event_name}</p>
                <p>Location: {event.location}</p>
                <p>Price: ${event.price}</p>
                <p>Event Date: {event.event_date}</p>
                {eventHasPassed(event.event_date) && (
                  <div className="review-form-container">
                    <h3 className="h3teksti">Leave a Review</h3>
                    {alreadyReviewedMessage ? (
                      <p>{alreadyReviewedMessage}</p>
                    ) : (
                      <>
                        <label>Rating:</label>
                        <div className="star-rating-container">
                          {[1, 2, 3, 4, 5].map((star) => (
                            <label key={star}>
                              <input
                                type="radio"
                                name="rating"
                                value={star}
                                checked={review.rating === star}
                                onChange={() => setReview({ ...review, rating: star })}
                              />
                              {star}
                            </label>
                          ))}
                        </div>
                        <button onClick={() => handleLeaveReview(event.id)}>Submit Review</button>
                      </>
                    )}
                  </div>
                )}
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default PastEventsPage;
