// Login.jsx

import React, { useState } from 'react';
import './style/Login.css';
import { useNavigate } from 'react-router-dom';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

 // ... (previous code)

 const handleLogin = async (e) => {
  e.preventDefault();

  console.log('Username:', username);
  console.log('Password:', password);

  const formData = new FormData();
  formData.append('username', username);
  formData.append('password', password);

  try {
    const response = await fetch('http://localhost:8888/api/api.php', {
      method: 'POST',
      body: formData,
    });

    if (!response.ok) {
      console.error('Request failed with status:', response.status);
      setError('Invalid username or password.');
      return;
    }

    const responseBody = await response.json();
    console.log('Response Body:', responseBody);

    // Handle the response and redirect logic as before...

  } catch (error) {
    console.error('Error:', error);
    setError('An unexpected error occurred.');
  }
};



// ... (rest of the code)


  return (
    <div className="login-main">
      <div className="login-center">
        <div className="login-image"></div>
        <div className="loginTOP">
          <div className="login-logo"></div>
          <div className="login-title">
            <h1 className="register-h1">LOGIN</h1>
          </div>
          <form className="login-form" onSubmit={handleLogin}>
            <div className="inputFI">
              <input
                type="text"
                placeholder="Username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
            </div>
            <div className="inputFI2">
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            {error && <div className="error-message">{error}</div>}
            <div className="buttonss">
              <button type="submit" className="button1">
                LOGIN
              </button>
            </div>
            <div className="loginB"></div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Login;
