// EventDetailsPage.jsx
import React, { useState, useEffect } from 'react';

const EventDetailsPage = ({ eventId }) => {
  const [event, setEvent] = useState(null);
  const [averageRating, setAverageRating] = useState(0);
  const [comments, setComments] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchEventDetails = async () => {
      try {
        // Make sure eventId is defined before making the request
        if (!eventId) {
          console.error('eventId is undefined');
          setLoading(false);
          return;
        }

        const response = await fetch(`http://localhost/api/getEventDetails.php?eventId=${eventId}`);

        if (!response.ok) {
          console.error(`HTTP error! Status: ${response.status}`);
          throw new Error('Error fetching event details.');
        }

        const data = await response.json();
        setEvent(data.event);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching event details:', error);
        setLoading(false);
      }
    };

    const fetchAverageRatingAndComments = async () => {
      try {
        // Make sure eventId is defined before making the request
        if (!eventId) {
          console.error('eventId is undefined');
          return;
        }

        const avgRatingResponse = await fetch(`http://localhost/api/getAverageEventRating.php?eventId=${eventId}`);

        if (!avgRatingResponse.ok) {
          console.error(`HTTP error! Status: ${avgRatingResponse.status}`);
          throw new Error('Error fetching average rating.');
        }

        const avgRatingData = await avgRatingResponse.json();
        setAverageRating(avgRatingData.averageRating);
      } catch (error) {
        console.error('Error fetching average rating:', error);
      }

      try {
        // Make sure eventId is defined before making the request
        if (!eventId) {
          console.error('eventId is undefined');
          return;
        }

        const commentsResponse = await fetch(`http://localhost/api/getEventComments.php?eventId=${eventId}`);

        if (!commentsResponse.ok) {
          console.error(`HTTP error! Status: ${commentsResponse.status}`);
          throw new Error('Error fetching comments.');
        }

        const commentsData = await commentsResponse.json();
        setComments(commentsData.comments || []); // Handle the case where comments is undefined
      } catch (error) {
        console.error('Error fetching comments:', error);
      }
    };

    fetchEventDetails();
    fetchAverageRatingAndComments();
  }, [eventId]);

  return (
    <div>
      {loading ? (
        <p>Loading...</p>
      ) : event ? (
        <div>
          <h2>{event.event_name}</h2>
          <p>Location: {event.location}</p>
          <p>Price: ${event.price}</p>
          <p>Event Date: {event.event_date}</p>
          <img src={event.image_link} alt={event.event_name} />

          <div>
            <h3>Average Rating: {averageRating}</h3>
            <h4>Comments:</h4>
            <ul>
              {comments.map((comment) => (
                <li key={comment.id}>{comment.comment}</li>
              ))}
            </ul>
          </div>
        </div>
      ) : (
        <p>Event not found.</p>
      )}
    </div>
  );
};

export default EventDetailsPage;
