// EventDetails.jsx
import React, { useState, useEffect } from 'react';

const EventDetails = ({ eventId }) => {
  const [averageRating, setAverageRating] = useState(0);
  const [comments, setComments] = useState([]);

  useEffect(() => {
    const fetchAverageRatingAndComments = async () => {
      try {
        const response = await fetch(`http://localhost/api/getAverageEventRating.php?eventId=${eventId}`);
        if (!response.ok) {
          console.error(`HTTP error! Status: ${response.status}`);
          throw new Error('Error fetching average rating.');
        }

        const data = await response.json();
        setAverageRating(data.averageRating);
      } catch (error) {
        console.error('Error fetching average rating:', error);
      }

      try {
        const response = await fetch(`http://localhost/api/getEventComments.php?eventId=${eventId}`);
        if (!response.ok) {
          console.error(`HTTP error! Status: ${response.status}`);
          throw new Error('Error fetching comments.');
        }

        const data = await response.json();
        setComments(data.comments);
      } catch (error) {
        console.error('Error fetching comments:', error);
      }
    };

    fetchAverageRatingAndComments();
  }, [eventId]);

  return (
    <div>
      <h3>Average Rating: {averageRating}</h3>
      <h4>Comments:</h4>
      <ul>
        {comments.map((comment) => (
          <li key={comment.id}>{comment.comment}</li>
        ))}
      </ul>
    </div>
  );
};

export default EventDetails;
