import React, { useState, useEffect, useContext } from 'react';
import { UserContext } from './UserContext';
import { useNavigate } from 'react-router-dom';
import Nav from './Nav';
import './style/EventReviewPage.css'; // Create a CSS file for styling
import jsPDF from 'jspdf';

const EventReviewPage = () => {
  const { isAuthenticated, userData } = useContext(UserContext);
  const navigate = useNavigate();
  const [eventsToReview, setEventsToReview] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');

        if (!token) {
          console.error('Token not found in local storage. Redirecting to login.');
          navigate('/login');
          return;
        }

        if (!isAuthenticated || !userData || !userData.userId) {
          console.error('Authentication information is missing. Redirecting to login.');
          navigate('/login');
          return;
        }

        const response = await fetch(`http://localhost:8888/api/getEventsToReview.php?userId=${userData.userId}`, {
          method: 'GET',
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        });

        if (!response.ok) {
          console.error(`HTTP error! Status: ${response.status}`);
          throw new Error('Error fetching events to review.');
        }

        const data = await response.json();

        setEventsToReview(data.eventsToReview);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching events to review:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, [navigate, isAuthenticated, userData]);

  const handleReviewSubmit = async (eventData, reviewText, rating) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:8888/api/submitEventReview.php', {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          userId: userData.userId,
          eventId: eventData.id,
          reviewText,
          rating,
        }),
      });

      if (!response.ok) {
        console.error(`HTTP error! Status: ${response.status}`);
        throw new Error('Error submitting event review.');
      }

      // Handle success, e.g., remove the event from the eventsToReview state
      const updatedEventsToReview = eventsToReview.filter((event) => event.id !== eventData.id);
      setEventsToReview(updatedEventsToReview);

      console.log('Event review submitted successfully.');
    } catch (error) {
      console.error('Error submitting event review:', error);
    }
  };

  return (
    <div>
      <Nav />
      <div className="event-review-page-main-container">
        <h2>Event Reviews</h2>
        <div className="events-to-review-container">
          {eventsToReview.map((event) => (
            <div key={event.id} className="event-to-review">
              <p>Event Name: {event.event_name}</p>
              <p>Location: {event.location}</p>
              <p>Event Date: {event.event_date}</p>
              <p>Rating: {event.rating}</p>
              <p>Review: {event.reviewText}</p>
              <textarea
                placeholder="Write your review here..."
                onChange={(e) => setReviewText(e.target.value)}
                value={reviewText}
              />
              <button onClick={() => handleReviewSubmit(event, reviewText, rating)}>
                Submit Review
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default EventReviewPage;
