// EventReviewsPage.jsx

import React, { useState, useEffect } from 'react';

const EventReviewsPage = () => {
  const [eventsWithRatings, setEventsWithRatings] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchEventsWithRatings = async () => {
    try {
      const response = await fetch('http://localhost/api/getRatedEvents.php');

      if (!response.ok) {
        console.error(`HTTP error! Status: ${response.status}`);
        throw new Error('Error fetching rated events.');
      }

      const data = await response.json();

      if (Array.isArray(data) && data.length > 0) {
        // Filter out events without ratings
        const eventsWithRatingsAndReviews = data.filter(
          (event) => event.average_rating !== null && event.average_rating !== undefined
        );
        setEventsWithRatings(eventsWithRatingsAndReviews);
      } else {
        console.log('No events with ratings found.');
      }

      setLoading(false);
    } catch (error) {
      console.error('Error fetching rated events:', error);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchEventsWithRatings();
  }, []);

  return (
    <div>
      {loading ? (
        <p>Loading events with ratings...</p>
      ) : (
        <div>
          {eventsWithRatings.length > 0 ? (
            <ul>
              {eventsWithRatings.map((event) => (
                <li key={event.event_id}>
                  <h3>{event.event_name}</h3>
                  {event.image_link && <img src={event.image_link} alt={`${event.event_name} Image`} />}
                  <p>Rating: {Number(event.average_rating).toFixed(2)}</p>
                  {event.reviews && event.reviews.length > 0 ? (
                    <ul>
                      {event.reviews.map((review) => (
                        <li key={review.id}>
                          <p>User Rating: {review.rating}</p>
                          {review.comment && <p>User Comment: {review.comment}</p>}
                          
                        </li>
                      ))}
                    </ul>
                  ) : (
                    <p>No user reviews for this event.</p>
                  )}
                </li>
              ))}
            </ul>
          ) : (
            <p>No events with ratings found.</p>
          )}
        </div>
      )}
    </div>
  );
};

export default EventReviewsPage;
