// UserContext.js
import React, { createContext, useState, useEffect } from 'react';

const UserContext = createContext();

const UserProvider = ({ children }) => {
  const [authStatus, setAuthStatus] = useState(false);
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    // Check if user is authenticated based on your logic
    // For example, you can check if there is a token in localStorage
    const isAuthenticated = localStorage.getItem('token') !== null;

    setAuthStatus(isAuthenticated);

    // If authenticated, fetch user data from the server and update context
    if (isAuthenticated) {
      // You can fetch user data here
      // For now, I'll just set a sample user
      setUserData({
        userId: 1,
        username: 'admin',
        role: 'Admin',
      });
    }
  }, []);

  return (
    <UserContext.Provider value={{ authStatus, setAuthStatus, userData, setUserData }}>
      {children}
    </UserContext.Provider>
  );
};

export { UserContext, UserProvider };
