// EventDetailsPage.jsx
import React, { useState, useEffect } from 'react';
import EventDetails from './EventDetails';

const EventDetailsPage = ({ eventId }) => {
  const [event, setEvent] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchEventDetails = async () => {
      try {
        const response = await fetch(`http://localhost/api/getEventDetails.php?eventId=${eventId}`);
        if (!response.ok) {
          console.error(`HTTP error! Status: ${response.status}`);
          throw new Error('Error fetching event details.');
        }

        const data = await response.json();
        setEvent(data.event);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching event details:', error);
        setLoading(false);
      }
    };

    fetchEventDetails();
  }, [eventId]);

  return (
    <div>
      {loading ? (
        <p>Loading...</p>
      ) : event ? (
        <div>
          <h2>{event.event_name}</h2>
          <p>Location: {event.location}</p>
          <p>Price: ${event.price}</p>
          <p>Event Date: {event.event_date}</p>
          <img src={event.image_link} alt={event.event_name} />

          {/* Display the average rating and comments using the EventDetails component */}
          <EventDetails eventId={eventId} />

          {/* You can add styling to hide the comments initially */}
          <div className="comments-container" style={{ display: 'none' }}>
            <h3>Comments:</h3>
            {event.comments.map((comment) => (
              <p key={comment.id}>{comment.comment}</p>
            ))}
          </div>
        </div>
      ) : (
        <p>Event not found.</p>
      )}
    </div>
  );
};

export default EventDetailsPage;
