import React, { useState, useEffect } from 'react';
import Nav from './Nav';

const AverageRatingPage = () => {
  const [eventsWithRatings, setEventsWithRatings] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchEventsWithRatings = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await fetch('http://localhost/api/getRatedEvents.php', {
          method: 'GET',
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        });

        if (!response.ok) {
          console.error(`HTTP error! Status: ${response.status}`);
          console.log('Response content:', await response.text());
          throw new Error('Error fetching rated events.');
        }

        const contentType = response.headers.get('content-type');
        const isJson = contentType && contentType.includes('application/json');

        if (!isJson) {
          console.error('Unexpected response content type. Expected JSON.');
          console.log('Response content:', await response.text());
          throw new Error('Invalid response format.');
        }

        const data = await response.json();

        if (Array.isArray(data) && data.length > 0) {
          setEventsWithRatings(data);
        } else {
          console.log('No rated events found.');
        }

        setLoading(false);
      } catch (error) {
        console.error('Error fetching rated events:', error);
        setLoading(false);
      }
    };

    fetchEventsWithRatings();
  }, []);

  return (
    <div className="average-rating-page">
      <Nav />

      <div className="average-rating-page-main-container">
        <h2>Average Ratings for Rated Events</h2>
        <div className="average-rating-container">
          {loading ? (
            <p className="loading-message">Loading...</p>
          ) : eventsWithRatings.length > 0 ? (
            eventsWithRatings.map((event) => (
              <div key={event.id} className="average-rating-item-container">
                <h3>{event.event_name}</h3>
                <p>Location: {event.location}</p>
                <p>Price: ${event.price}</p>
                <p>Event Date: {event.event_date}</p>
                <img src={event.image_link} alt={event.event_name} className="event-image" />

                {event.average_rating ? (
                  <div>
                    <p>Average Rating: {event.average_rating.toFixed(2)}</p>
                    <p>Reviews:</p>
                    <ul>
                      {event.reviews.map((review) => (
                        <li key={review.id}>
                          <p>Rating: {review.rating}</p>
                          <p>Comment: {review.comment}</p>
                        </li>
                      ))}
                    </ul>
                  </div>
                ) : (
                  <p>No reviews for this event.</p>
                )}
              </div>
            ))
          ) : (
            <p className="no-events-message">No rated events found.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default AverageRatingPage;
