import React from 'react';
import Nav from './Nav';
import Footer from './Footer';

const AboutUsPage = () => {
  return (
    <div className="about-us-page">
      <Nav />

      <section className="about-us-section">
        <h2>About Us</h2>
        <p>
          Welcome to Eventus! We are passionate about bringing people together through the universal language of music.
          Our platform is designed to provide a diverse range of musical experiences, from thrilling concerts to intimate performances.
          Whether you're a dedicated music enthusiast or just starting your musical journey, Eventus is here to help you discover and
          enjoy the perfect soundtrack for your soul.
        </p>
        <p>
          At Eventus, we believe in the power of live music to create lasting memories. Our mission is to connect music lovers with
          incredible events, making it easy for you to find and attend concerts that match your tastes. Explore our curated selection
          of genres, from the energetic beats of Hip Hop to the soulful melodies of Jazz, and find the events that resonate with you.
        </p>
      </section>

      <section className="team-section">
        <h2>Meet the Team</h2>
        <div className="team-member">
          <div className="team-member-image-container">
            {/* You can add an image source for each team member */}
          </div>
          <div className="team-member-info">
            <h3>John Doe</h3>
            <p>Co-Founder & CEO</p>
            {/* Add more details about the team member */}
          </div>
        </div>
        {/* Add more team members as needed */}
      </section>

      <Footer />
    </div>
  );
};

export default AboutUsPage;
