import React, { useEffect, useState } from 'react';
import './style/Login.css';
import EditModal from './EditModal';
import { FaSearch, FaEdit } from 'react-icons/fa'; // Import relevant icons
import { useNavigate } from 'react-router-dom';
const User = () => {
    const [users, setUsers] = useState([]);
    const [selectedUser, setSelectedUser] = useState(null);
  const [userCount, setUserCount] = useState(0);
  const [availableProfessions] = useState(['Noliktavas darbinieks', 'Plauktu Kārtotājs']);
  const [editModalVisible, setEditModalVisible] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [fetchError, setFetchError] = useState(null);

  // Debounce function to limit the rate of calls
  const debounce = (func, delay) => {
    let timeoutId;
    return function (...args) {
      clearTimeout(timeoutId);
      timeoutId = setTimeout(() => func(...args), delay);
    };
  };

  // Declare fetchUsers function
  const fetchUsers = async () => {
    try {
      console.log('Fetching users with query:', searchQuery);
      let apiUrl = 'http://localhost:8888/api/getUsers.php';
      let requestOptions = {};

      if (searchQuery) {
        console.log('Search Query:', searchQuery);
        apiUrl = 'http://localhost:8888/api/searchUsers.php';
        requestOptions = {
          method: 'POST',
          headers: {
            'Content-Type': 'application/x-www-form-urlencoded',
          },
          body: new URLSearchParams({
            searchQuery: searchQuery,
          }),
        };
      }

      const response = await fetch(apiUrl, requestOptions);
      const data = await response.json();

      console.log('Response from backend:', data);

      if (data.success) {
        setUsers(data.users);
        setFetchError(null); // Clear any previous fetch errors
      } else {
        console.error('Error fetching users:', data.message);
        setFetchError('Error fetching users. Please try again.'); // Set the error in state
        console.log('Fetch error:', fetchError); // Add this line to log the error
      }
    } catch (error) {
      console.error('Fetch error:', error);
      setFetchError('An unexpected error occurred. Please try again.'); // Set the error in state
      console.log('Fetch error:', fetchError); // Add this line to log the error
    }
  };

  // Use fetchUsers inside useEffect
  useEffect(() => {
    console.log('useEffect triggered with searchQuery:', searchQuery);
    fetchUsers().catch((error) => console.error('Error fetching users:', error));
    fetchUserCount();
  }, [searchQuery, fetchUsers]); // Add fetchUsers to the dependency array



  // Declare debouncedFetchUsers after fetchUsers
  const debouncedFetchUsers = debounce(fetchUsers, 300); // Adjust the delay as needed

  const fetchUserCount = async () => {
    try {
      const response = await fetch('http://localhost:8888/api/getUserCount.php');
      const data = await response.json();

      if (data.success) {
        setUserCount(data.count);
      } else {
        console.error('Error fetching user count:', data.message);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handlePromoteDemote = async (userId, newProfession) => {
    try {
      const response = await fetch('http://localhost:8888/api/promoteDemoteUser.php', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          userId: userId,
          action: 'promote',
          newProfession: newProfession,
        }),
      });
      const data = await response.json();

      if (data.success) {
        fetchUsers();
        setSelectedUser(null);
      } else {
        console.error('Error promoting/demoting user:', data.message);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleAtlaist = async () => {
    if (!selectedUser) {
      console.error('No user selected');
      return;
    }

    try {
      const response = await fetch('http://localhost:8888/api/atlaistUser.php', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          userId: selectedUser.id,
        }),
      });
      const data = await response.json();

      if (data.success) {
        fetchUsers();
        setSelectedUser(null);
      } else {
        console.error('Error dismissing user:', data.message);
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleEdit = () => {
    if (!selectedUser) {
      console.error('No user selected');
      return;
    }

    console.log('Selected user:', selectedUser);
    setEditModalVisible(true);
  };

  const handleEditModalSave = async (editedData) => {
    const { id, ...restData } = editedData;

    if (Object.keys(restData).length === 0) {
      console.error('No fields were edited');
      return;
    }

    console.log('Data to be sent:', { userId: selectedUser.id, updatedFields: restData });

    try {
      const response = await fetch('http://localhost:8888/api/updateUser.php', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          userId: selectedUser.id,
          updatedFields: restData,
        }),
      });

      const data = await response.json();
      console.log('Response data:', data);

      if (data.success) {
        setUsers((prevUsers) => {
          const updatedUsers = prevUsers.map((user) =>
            user.id === selectedUser.id ? { ...user, ...restData } : user
          );
          return updatedUsers;
        });

        setEditModalVisible(false);
        setSelectedUser(null);
      } else {
        console.error('Error updating user data:', data.message);

        if (data.errors) {
          alert('Validation errors:\n' + Object.values(data.errors).join('\n'));
        }
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleSaveChanges = async () => {
    try {
      console.log('Saving changes to the server...');
    } catch (error) {
      console.error('Error saving changes:', error);
    }
  };

  return (
    <div className='User-main'>
      <div className='user-text'>
        <h1 className='user-lietotaji'>User Management</h1>
        {fetchError && (
          <div className="error-message">
            {fetchError}
          </div>
        )}
        <div className='user-search-bar-container'>
          <input
            type='text'
            placeholder='Search users...'
            value={searchQuery}
            onChange={(e) => {
              setSearchQuery(e.target.value);
              debouncedFetchUsers(); // Use debounced function here
            }}
            className='user-search-bar-input'
          />
          <div className='search-icon'>
            <FaSearch />
          </div>
        </div>
      </div>
      <div className='user-roles-container'>
        <div className='user-roles-inside'>
          <h2 className='user-roles-text'>Name</h2>
        </div>
        <div className='user-roles-inside'>
          <h2 className='user-roles-text'>Role</h2>
        </div>
        <div className='user-roles-inside1'>
          <h2 className='user-roles-text'>Actions</h2>
        </div>
      </div>
      <div className='user-counter'>Showing {userCount} Users</div>
      <div className='user-center'>
        {users
          .filter((user) =>
            user.first_name.toLowerCase().includes(searchQuery.toLowerCase())
          )
          .map((user) => (
            <div
              key={user.id}
              className={`user-center-info-container ${
                selectedUser === user ? 'selected-user' : ''
              }`}
              onClick={() => setSelectedUser(user)}
            >
              <div className='user-name'>
                <div className='user-info'>
                  <div>{user.first_name}</div>
                </div>
                <div className='user-info'>
                  <div className='user-info user-last-name'>{user.last_name}</div>
                </div>
              </div>
              <div className='user-name'>
  <div className="user-custom-dropdown">
    <select
      className='user-dropdown'
      value={user.role_id || ''}  // Use user.role_id
      onChange={(e) => handlePromoteDemote(user.id, e.target.value)}
    >
      {availableProfessions.map((profession) => (
        <option key={profession} value={profession}>
          {profession}
        </option>
      ))}
    </select>
    <div className="dropdown-arrow1">&#9660;</div>
  </div>
</div>
              <div className='user-name'>
                <button
                  className='user-button settings-icon'
                  onClick={handleAtlaist}
                >
                  Remove User
                </button>
              </div>
              <div className='user-name'>
                <button
                  className='user-button-edit'
                  onClick={handleEdit}
                >
                  <FaEdit /> Modify Data
                </button>
              </div>
            </div>
          ))}
      </div>
      <div className='user-button-saglabat'>
        <button className='user-button-save' onClick={handleSaveChanges}>
          SAGLABĀT
        </button>
      </div>

      {editModalVisible && (
        <EditModal user={selectedUser} onSave={handleEditModalSave} onCancel={() => setEditModalVisible(false)} />
      )}
    </div>
  );
};

export default User;
