import React, { useState } from 'react';
import './style/Login.css';
import { useNavigate } from 'react-router-dom';
import { useUser } from './UserContext';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const { login } = useUser();

  // ...

const handleLogin = async (e) => {
  e.preventDefault();

  // Basic frontend validation
  if (!username || !password) {
    setError('Please enter both username and password.');
    return;
  }

  try {
    const response = await fetch('http://localhost:8888/api/api.php', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        username: username,
        password: password,
      }),
    });

    console.log('Response status:', response.status);

    if (!response.ok) {
      console.error('Request failed with status:', response.status);
      setError('Invalid username or password.');
      return;
    }

    const data = await response.text();
    console.log('Raw response data:', data);

    const regex = /\{.*?\}/g;
    const jsonResponses = data.match(regex);

    if (!jsonResponses) {
      console.error('No valid JSON found in the response.');
      setError('An unexpected error occurred. Please try again later.');
      return;
    }

    // Use Promise.all to wait for all asynchronous operations to complete
    await Promise.all(
      jsonResponses.map(async (jsonData) => {
        try {
          const parsedData = JSON.parse(jsonData);
          console.log('Parsed JSON data:', parsedData);

          if (parsedData.success) {
            console.log('Login successful');
            console.log('isAdmin:', parsedData.isAdmin);

            // Retrieve existing sessions from localStorage
            const sessions = JSON.parse(localStorage.getItem('sessions')) || [];

            // Create a new session object
            const newSession = {
              username: parsedData.username,
              role: parsedData.role,
              isAdmin: parsedData.isAdmin, // Ensure to set isAdmin correctly
            };

            // Add the new session to the array
            sessions.push(newSession);

            // Update the sessions in localStorage
            localStorage.setItem('sessions', JSON.stringify(sessions));

            // Update the user context
            login({
              username: parsedData.username,
              role: parsedData.role,
            });

            // Redirect based on user role
            if (parsedData.isAdmin) {
              console.log('Redirecting to admin page');
              navigate('/admin');
            } else {
              console.log('Redirecting to users page');
              navigate('/users');
            }
          } else {
            console.error('Login failed:', parsedData.message);
            setError('Invalid username or password.');
          }

        } catch (jsonError) {
          console.error('Error parsing JSON:', jsonError);
          setError('An unexpected error occurred. Please try again later.');
        }
      })
    );
  } catch (error) {
    console.error('Error during login:', error);
    setError('An unexpected error occurred. Please try again later.');
  }
};

  return (
    <div className="login-main">
      <div className="login-center">
        <div className="login-image"></div>
        <div className="loginTOP">
          <div className="login-logo"></div>
          <div className="login-title">
            <h1 className="register-h1">LOGIN</h1>
          </div>
          <form className="login-form" onSubmit={handleLogin}>
            <div className="inputFI">
              <input
                type="text"
                placeholder="Username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
            </div>
            <div className="inputFI2">
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            {error && <div className="error-message">{error}</div>}
            <div className="buttonss">
              <button type="submit" className="button1">
                LOGIN
              </button>
            </div>
            <div className="loginB"></div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Login;
