import React, { useState, useEffect, useContext } from 'react';
import { UserContext } from './UserContext';
import { useNavigate } from 'react-router-dom';
import Nav from './Nav';
import './style/OrderPage.css';
import jsPDF from 'jspdf';
import 'jspdf-autotable';
import QRCode from 'qrcode';

const OrdersPage = () => {
  const { isAuthenticated, userData } = useContext(UserContext);
  const navigate = useNavigate();
  const [cart, setCartState] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const token = localStorage.getItem('token');

        if (!token) {
          console.error('Token not found in local storage. Redirecting to login.');
          navigate('/login');
          return;
        }

        if (!isAuthenticated || !userData || !userData.userId) {
          console.error('Authentication information is missing. Redirecting to login.');
          navigate('/login');
          return;
        }

        const response = await fetch(`http://localhost:8888/api/getCartItems.php?userId=${userData.userId}`, {
          method: 'GET',
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        });

        if (!response.ok) {
          console.error(`HTTP error! Status: ${response.status}`);
          throw new Error('Error fetching cart items.');
        }

        const data = await response.json();

        setCartState(data.cartItems);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching cart items:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, [navigate, isAuthenticated, userData]);

  const handlePurchase = async (cartItemId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:8888/api/updateCartItemStatus.php?cartItemId=${cartItemId}`, {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        console.error(`HTTP error! Status: ${response.status}`);
        throw new Error('Error updating cart item status.');
      }

      const updatedCart = cart.map((item) =>
        item.id === cartItemId ? { ...item, status: 'purchased' } : item
      );

      setCartState(updatedCart);
    } catch (error) {
      console.error('Error updating cart item status:', error);
    }
  };

  const handleDownloadPDF = (eventData) => {
    const purchaseDate = new Date().toLocaleString();
    const randomCode = generateRandomCode();
    generatePDF(eventData, purchaseDate, randomCode);
  };

  const generateRandomCode = () => {
    const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    const length = 8;
    let randomCode = '';

    for (let i = 0; i < length; i++) {
      randomCode += characters.charAt(Math.floor(Math.random() * characters.length));
    }

    return randomCode;
  };

  const generatePDF = (eventData, purchaseDate, randomCode) => {
    try {
      const pdf = new jsPDF();

      // Title
      pdf.setFontSize(20);
      pdf.text('Eventus Concerts', 20, 20);

      // User Name
      pdf.setFontSize(14);
      pdf.text(`Username: ${userData.username}`, 20, 30);

      // Concert Details
      pdf.text(`Concert Name: ${eventData.event_name}`, 20, 40);
      pdf.text(`Price: $${eventData.price}`, 20, 50);
      pdf.text(`Concert Date: ${eventData.event_date}`, 20, 60);
      pdf.text(`Location: ${eventData.location}`, 20, 70);

      // QR Code directly in the HTML structure
      const canvas = document.createElement('canvas');

      // Use qrcode library directly
      QRCode.toCanvas(canvas, `Eventus Concerts\nUsername: ${userData.username}\nConcert: ${eventData.event_name}\nPrice: $${eventData.price}\nDate: ${eventData.event_date}\nLocation: ${eventData.location}\nCode: ${randomCode}`)
        .then(() => {
          pdf.addImage(canvas.toDataURL('image/jpeg'), 'JPEG', 150, 80, 40, 40);

          // Random Code
          pdf.text(`Random Code: ${randomCode}`, 20, 130);

          pdf.save(`ticket_${eventData.id}_${purchaseDate}.pdf`);
        })
        .catch((error) => {
          console.error('Error generating QR Code:', error);
        });
    } catch (error) {
      console.error('Error generating PDF:', error);
    }
  };

  return (
    <div>
      <Nav />
      <div className="orders-page-main-container">
        <h2>Your Orders</h2>
        <div className="orders-container">
          {cart.map((item) => (
            <div key={item.id} className="order-item">
              <p>Event Name: {item.event_name}</p>
              <p>Location: {item.location}</p>
              <p>Price: ${item.price}</p>
              <p>Tickets Amount: {item.tickets_amount}</p>
              {item.status === 'purchased' && (
                <>
                  {/* QR Code directly in the HTML structure */}
                  <QRCode value={`Eventus Concerts\nUsername: ${userData.username}\nConcert: ${item.event_name}\nPrice: $${item.price}\nDate: ${item.event_date}\nLocation: ${item.location}\nCode: ${generateRandomCode()}`} />
                  <button className="download-pdf-button" onClick={() => handleDownloadPDF(item)}>
                    Download PDF
                  </button>
                </>
              )}
              {item.status !== 'purchased' && (
                <button className="purchase-button" onClick={() => handlePurchase(item.id)}>
                  Purchase
                </button>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default OrdersPage;
