// EventReviewPage.js

import React, { useState } from 'react';
import Nav from './Nav';

const EventReviewPage = ({ event }) => {
  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState('');

  const handleRatingChange = (newRating) => {
    setRating(newRating);
  };

  const handleCommentChange = (e) => {
    setComment(e.target.value);
  };

  const handleSubmitReview = () => {
    // TODO: Implement the logic to submit the review to your backend
    console.log('Submitting review:', { rating, comment, event });
    // You can send this data to your server to store the review
  };

  return (
    <div>
      <Nav />
      <div className="review-page-main-container">
        <h2>Review Event: {event.event_name}</h2>
        <div>
          <label>Rating:</label>
          <select value={rating} onChange={(e) => handleRatingChange(e.target.value)}>
            <option value="1">1 Star</option>
            <option value="2">2 Stars</option>
            <option value="3">3 Stars</option>
            <option value="4">4 Stars</option>
            <option value="5">5 Stars</option>
          </select>
        </div>
        <div>
          <label>Comment:</label>
          <textarea value={comment} onChange={handleCommentChange} />
        </div>
        <button onClick={handleSubmitReview}>Submit Review</button>
      </div>
    </div>
  );
};

export default EventReviewPage;
