import React, { useState, useEffect } from 'react';
import './style/EventPage.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faShoppingCart } from '@fortawesome/free-solid-svg-icons';
import Nav from './Nav';
import EventForm from './EventForm';

const EventPage = ({ addToCart }) => {
  const [selectedDate, setSelectedDate] = useState('');
  const [futureDates, setFutureDates] = useState([]);
  const [isHovered, setIsHovered] = useState(false);
  const [isFormVisible, setIsFormVisible] = useState(false);

  useEffect(() => {
    const numberOfYears = 1;
    const currentYear = new Date().getFullYear();
    const futureDatesArray = [];

    for (let year = currentYear; year < currentYear + numberOfYears; year++) {
      for (let month = 0; month < 12; month++) {
        const monthName = new Date(year, month).toLocaleString('default', { month: 'long' });
        const dateString = `${year}-${month + 1 < 10 ? '0' : ''}${month + 1} ${monthName}`;
        futureDatesArray.push(dateString);
      }
    }

    setFutureDates(futureDatesArray);
  }, []);

  const handleAddEvent = (eventData) => {
    console.log('Adding event:', eventData);
    fetch('http://localhost:8888/api/EventApi.php', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        event_name: eventData.eventName,
        event_date: eventData.eventDate,
        location: eventData.location,
        tickets_available: eventData.ticketsAvailable,
        price: eventData.price,
        imageLink: eventData.imageLink,
      }),
    })
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
        console.log('Success:', data);
      })
      .catch(error => {
        console.error('Error:', error);
        console.error('Response:', error.response);
        if (error.response) {
          // Log the response text for debugging
          error.response.text().then(text => console.error('Response Text:', text));
        } else {
          console.error('Response not available.');
        }
      });

    setIsFormVisible(false); // Close the form after submission
  };




  return (
    <div>
      <Nav />
      <div className="event-page-main-container">
        <div className="search-container">
          <div className="date-dropdown-container">
            <select
              id="dateSelect"
              value={selectedDate}
              onChange={(e) => setSelectedDate(e.target.value)}
            >
              <option value="" disabled>
                Select a date
              </option>
              {futureDates.map((date) => (
                <option key={date} value={date}>
                  {date}
                </option>
              ))}
            </select>
          </div>
          <div className="search-input-container">
            <input className="search-input" type="text" placeholder="Search events" />
            <button>SORT</button>
            {/* visible to admins only */}
            <button className='add-event' onClick={() => setIsFormVisible(true)}>ADD EVENT</button>
          </div>
        </div>
        <div className="event-info-second">
          <div
            className="event-info-container"
            onMouseEnter={() => setIsHovered(true)}
            onMouseLeave={() => setIsHovered(false)}
          >
            <div className="event-image-container">
              <img
                className="event-image"
                src="https://i.scdn.co/image/ab67616d0000b27300dfa020bf12e7b8704d2d36"
                alt="Image Description"
              />
              {isHovered && (
                <button className="add-to-cart-button" onClick={() => console.log('Added to Cart')}>
                  <FontAwesomeIcon icon={faShoppingCart} /> Add to Cart
                </button>
              )}
            </div>
            <div className="event-text-container">
              <p className="event-info">Location: Concert Venue</p>
              <p className="event-info">Date: 2024.14.05</p>
              <p className="event-info">Concert Name: Goda VƒÅrds 5</p>
              <p className="event-info">Performing: Artist(s) Mauku Sencis</p>
              <p className="event-info">Tickets Available: 10</p>
              <p className="event-info">Price:10$</p>
            </div>
          </div>
        </div>
      </div>

      {isFormVisible && <EventForm addEvent={handleAddEvent} />}
    </div>
  );
};

export default EventPage;
