import React, { useState } from 'react';
import Nav from './Nav';
import './style/review.css';

const ReviewPage = () => {
  const [isExpanded, setExpanded] = useState(false);
  const [currentReviewPage, setCurrentReviewPage] = useState(1);
  const reviewsPerPage = 3; // Number of reviews to display per page

  const toggleExpand = () => {
    setExpanded(!isExpanded);
  };

  // Dummy reviews data for testing
  const reviews = [
    { id: 1, eventName: 'Event 1', stars: 3 },
    { id: 2, eventName: 'Event 2', stars: 4 },
    { id: 3, eventName: 'Event 3', stars: 5 },
    { id: 4, eventName: 'Event 4', stars: 4 },
    { id: 5, eventName: 'Event 5', stars: 2 },
    // Add more reviews as needed
  ];

  // Function to handle page change
  const handleReviewPageChange = (page) => {
    setCurrentReviewPage(page);
  };

  const startReviewIndex = (currentReviewPage - 1) * reviewsPerPage;
  const endReviewIndex = startReviewIndex + reviewsPerPage;

  return (
    <div className="review-container">
      {reviews.slice(startReviewIndex, endReviewIndex).map((review) => (
        <div key={review.id} className={`review-data ${isExpanded ? 'expanded' : ''}`} onClick={toggleExpand}>
          <div className="review-image"></div>
          <label className="review-label">{review.eventName}</label>
          <div className="review-stars">
            {Array.from({ length: review.stars }, (_, index) => (
              <span key={index} className="star">&#9733;</span>
            ))}
          </div>
        </div>
      ))}

      {/* Pagination for reviews */}
      <div className="pagination-container">
        {Array.from({ length: Math.ceil(reviews.length / reviewsPerPage) }).map((_, index) => (
          <button
            key={index}
            onClick={() => handleReviewPageChange(index + 1)}
            className={index + 1 === currentReviewPage ? 'active' : ''}
          >
            {index + 1}
          </button>
        ))}
      </div>
    </div>
  );
};

export default ReviewPage;
