// EventReviewsPage.jsx
import React, { useState, useEffect } from 'react';
import Nav from './Nav';

const EventReviewsPage = ({ eventId }) => {
    const [eventReviews, setEventReviews] = useState([]);
    const [loading, setLoading] = useState(true);
  
    const fetchEventReviews = async () => {
      try {
        const response = await fetch(`http://localhost/api/getEventReviews.php?eventId=${eventId}`);
  
        if (!response.ok) {
          console.error(`HTTP error! Status: ${response.status}`);
          throw new Error('Error fetching event reviews.');
        }
  
        const data = await response.json();
  
        if (Array.isArray(data) && data.length > 0) {
          setEventReviews(data);
        } else {
          console.log('No reviews found for this event.');
        }
  
        setLoading(false);
      } catch (error) {
        console.error('Error fetching event reviews:', error);
        setLoading(false);
      }
    };
  
    useEffect(() => {
      fetchEventReviews();
    }, [eventId]);

  return (
    <div className="event-reviews-page">
      <Nav />

      <div className="event-reviews-page-main-container">
        <h2>Event Reviews</h2>
        {loading ? (
          <p className="loading-message">Loading...</p>
        ) : eventReviews.length > 0 ? (
          eventReviews.map((review) => (
            <div key={review.id} className="event-review-item-container">
              <p className="review-info">User: {review.username}</p>
              <p className="review-info">Rating: {review.rating}</p>
              <p className="review-info">Comment: {review.comment}</p>
            </div>
          ))
        ) : (
          <p className="no-reviews-message">No reviews found for this event.</p>
        )}
      </div>
    </div>
  );
};

export default EventReviewsPage;
