import React, { useState, useEffect } from 'react';
import Nav from './Nav';

const EventDetails = ({ eventId }) => {
  // State for average rating
  const [averageRating, setAverageRating] = useState(0);

  // State for comments
  const [comments, setComments] = useState([]);

  useEffect(() => {
    // Fetch average rating
    const fetchAverageRating = async () => {
      try {
        const response = await fetch(`http://localhost/api/getAverageRating.php?eventId=${eventId}`);
        if (!response.ok) {
          console.error(`HTTP error! Status: ${response.status}`);
          throw new Error('Error fetching average rating.');
        }

        const data = await response.json();
        setAverageRating(data.averageRating);
      } catch (error) {
        console.error('Error fetching average rating:', error);
      }
    };

    // Fetch comments
    const fetchComments = async () => {
      try {
        const response = await fetch(`http://localhost/api/getEventComments.php?eventId=${eventId}`);
        if (!response.ok) {
          console.error(`HTTP error! Status: ${response.status}`);
          throw new Error('Error fetching comments.');
        }

        const data = await response.json();
        setComments(data.comments);
      } catch (error) {
        console.error('Error fetching comments:', error);
      }
    };

    fetchAverageRating();
    fetchComments();
  }, [eventId]);

  return (
    <div>
      <Nav />
      <h2>Event Details</h2>
      <p>Average Rating: {averageRating}</p>
      <h3>Comments:</h3>
      <ul>
        {comments.map((comment) => (
          <li key={comment.id}>{comment.comment}</li>
        ))}
      </ul>
    </div>
  );
};

export default EventDetails;
