import React, { useState } from 'react';
import './style/Login.css';

function Login() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault();

        try {
            const response = await fetch('http://localhost:8888/api/api.php', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    username: username,
                    password: password,
                }),
            });

            const data = await response.json();

            if (data.success) {
                // Login successful, you can redirect or perform other actions
                console.log('Login successful');
            } else {
                console.error('Login failed:', data.message);
                // Handle login failure, show error message or perform other actions
            }
        } catch (error) {
            console.error('Error:', error);
            // Handle unexpected errors, show error message or perform other actions
        }
    };

    return (
        <div className="login-main">
            <div className="login-center">
                <div className="login-image"></div>
                <div className="loginTOP">
                    <div className="login-logo"></div>
                    <div className="login-title">
                        <h1 className="register-h1">LOGIN</h1>
                    </div>
                    <form className="login-form" onSubmit={handleLogin}>
                        <div className="inputFI">
                            <input
                                type="text"
                                placeholder="Username"
                                value={username}
                                onChange={(e) => setUsername(e.target.value)}
                            />
                        </div>
                        <div className="inputFI2">
                            <input
                                type="password"
                                placeholder="Password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                            />
                        </div>
                        <div className="buttonss">
                            <button type="submit" className="button1">
                                LOGIN
                            </button>
                        </div>
                        <div className="loginB"></div>
                    </form>
                </div>
            </div>
        </div>
    );
}

export default Login;
