import React, { useState, useEffect } from 'react';
import './style/Login.css';

const EditModal = React.memo(({ user, onSave, onCancel, availableProfessions = [] }) => {
  const [editedData, setEditedData] = useState({
    firstName: user.first_name,
    lastName: user.last_name,
    username: user.username,
    phone: user.phone,
    email: user.email,
    password: user.password,
  });

  const [newRole, setNewRole] = useState(user.role_id || availableProfessions[0]?.id);

  const [errors, setErrors] = useState({
    firstName: '',
    lastName: '',
    username: '',
    phone: '',
    email: '',
    password: '',
  });

  useEffect(() => {
    setEditedData({
      firstName: user.first_name,
      lastName: user.last_name,
      username: user.username,
      phone: user.phone,
      email: user.email,
      password: user.password,
    });
  }, [user]);

  useEffect(() => {
    const userRoleId = user.role_id;
    const defaultRole = availableProfessions[0]?.id;

    if (userRoleId !== undefined) {
      setNewRole(userRoleId);
    } else if (defaultRole !== undefined) {
      setNewRole(defaultRole);
    }
  }, [user.role_id, availableProfessions]);

  const handleInputChange = (e) => {
    const { name, value, type } = e.target;

    if (type === 'number' && isNaN(value)) {
      return;
    }

    setEditedData((prevData) => ({ ...prevData, [name]: value }));
    setErrors((prevErrors) => ({ ...prevErrors, [name]: '' }));
  };

  const validateInputs = async () => {
    const newErrors = {};

    // First Name
    if (!/^[a-zA-Z]+$/.test(editedData.firstName.trim())) {
      newErrors.firstName = 'First Name should contain only letters';
    }

    // Last Name
    if (!/^[a-zA-Z]+$/.test(editedData.lastName.trim())) {
      newErrors.lastName = 'Last Name should contain only letters';
    }

    // Email
    if (!editedData.email.includes('@')) {
      newErrors.email = 'Email should contain @';
    } else {
      // Check if email exists in the database
      const emailExists = await checkEmailExists(editedData.email);
      if (emailExists) {
        newErrors.email = 'Email already exists';
      }
    }

    // Phone
    if (!/^\d{11}$/.test(editedData.phone)) {
      newErrors.phone = 'Phone should be 11 digits';
    } else {
      // Check if phone exists in the database
      const phoneExists = await checkPhoneExists(editedData.phone);
      if (phoneExists) {
        newErrors.phone = 'Phone number already exists';
      }
    }

    // Password
    if (editedData.password.length < 6) {
      newErrors.password = 'Password should be at least 6 characters long';
    }

    setErrors(newErrors);

    return Object.keys(newErrors).length === 0;
  };

  const checkEmailExists = async (email) => {
    // Call your server-side API to check email existence
    const response = await fetch('http://localhost:8888/api/updateUser.php', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ email }),
    });
    const data = await response.json();
    return data.exists;
  };

  const checkPhoneExists = async (phone) => {
    // Call your server-side API to check phone existence
    const response = await fetch('http://localhost:8888/api/updateUser.php', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ phone }),
    });
    const data = await response.json();
    return data.exists;
  };

  const handleSave = async () => {
    try {
      console.log('Edited Data:', editedData);
      console.log('New Role:', newRole);

      if (!(await validateInputs())) {
        console.log('Validation failed');
        return;
      }

      const response = await fetch('http://localhost:8888/api/updateUser.php', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          userId: user.id,
          updatedFields: editedData,
          newRole: newRole,
        }),
      });

      if (!response.ok) {
        throw new Error(`Request failed with status: ${response.status}`);
      }

      const data = await response.json();

      if (!data.success) {
        throw new Error(`Error updating user data: ${data.message}`);
      }

      onSave({
        userId: user.id,
        updatedFields: editedData,
        newRole: newRole,
      });
      onCancel();
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div className='edit-modal'>
      <div className="new-user-top"> <h2 className='edit-user-h2'>Edit User Profile Details</h2></div>
      <div className="new-user-main">
        {/* First Name */}
        <div className="new-user-text-input">First Name</div>
        <input className='new-user-input' type='text' name='firstName' value={editedData.firstName} onChange={handleInputChange} />
        {errors.firstName && <div className="error-message">{errors.firstName}</div>}

        {/* Last Name */}
        <div className="new-user-text-input">Last Name</div>
        <input className='new-user-input' type='text' name='lastName' value={editedData.lastName} onChange={handleInputChange} />
        {errors.lastName && <div className="error-message">{errors.lastName}</div>}

        {/* Username */}
        <div className="new-user-text-input">Username</div>
        <input className='new-user-input' type='text' name='username' value={editedData.username} onChange={handleInputChange} />
        {errors.username && <div className="error-message">{errors.username}</div>}

        {/* Phone */}
        <div className="new-user-text-input">Phone</div>
        <input className='new-user-input' type='text' name='phone' value={editedData.phone} onChange={handleInputChange} />
        {errors.phone && <div className="error-message">{errors.phone}</div>}

        {/* Email */}
        <div className="new-user-text-input">Email</div>
        <input className='new-user-input' type='text' name='email' value={editedData.email} onChange={handleInputChange} />
        {errors.email && <div className="error-message">{errors.email}</div>}

        {/* Password */}
        <div className="new-user-text-input">Password</div>
        <input className='new-user-input' type='password' name='password' value={editedData.password} onChange={handleInputChange} />
        {errors.password && <div className="error-message">{errors.password}</div>}

        {/* Role */}
        <div className="new-user-text-input">Role</div>
        <select
          value={newRole}
          onChange={(e) => setNewRole(e.target.value)}
        >
          {availableProfessions.map((profession) => (
            <option key={profession.id} value={profession.id}>
              {profession.name}
            </option>
          ))}
        </select>

        {/* Add more input fields as needed */}

        <div>
          {/* Save and Cancel buttons */}
          <div className='edit-save-button'>
            <button className='click-save-button' onClick={handleSave}>
              <span className="underline-text">Save Changes</span>
            </button>
          </div>
          <div className='edit-save-button'>
            <button className='click-save-button' onClick={onCancel}>
              <span className="underline-text">Cancel</span>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
});

export default EditModal;
