import React, { useState, useEffect, useContext } from 'react';
import { UserContext } from './UserContext';
import { useNavigate } from 'react-router-dom';
import Nav from './Nav';

const PastCartEventsPage = () => {
  const { isAuthenticated, userData } = useContext(UserContext);
  const navigate = useNavigate();
  const [pastCartEvents, setPastCartEvents] = useState([]);
  const [loading, setLoading] = useState(true);

  // State for the review popup
  const [showReviewPopup, setShowReviewPopup] = useState(false);
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState('');

  // Define fetchData function outside useEffect
  const fetchData = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost/api/getPastCartEvents.php?userId=${userData.userId}`, {
        method: 'GET',
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        console.error(`HTTP error! Status: ${response.status}`);
        throw new Error('Error fetching past cart events.');
      }

      const data = await response.json();

      if (Array.isArray(data) && data.length > 0) {
        setPastCartEvents(data);
      } else {
        console.log('No past cart events found.');
      }

      setLoading(false);
    } catch (error) {
      console.error('Error fetching past cart events:', error);
      setLoading(false);
    }
  };

  useEffect(() => {
    // Call fetchData function inside useEffect
    fetchData();
  }, [navigate, isAuthenticated, userData]);

  // Function to handle the "Leave a Review" button click
  const handleLeaveReview = (event) => {
    setSelectedEvent(event);
    setShowReviewPopup(true);
  };

  // Function to handle the review submission
  const handleReviewSubmit = async () => {
    // Make a request to save the review data
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost/api/submitReview.php', {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          userId: userData.userId,
          eventId: selectedEvent.id,
          rating,
          comment,
        }),
      });

      if (!response.ok) {
        console.error(`HTTP error! Status: ${response.status}`);
        throw new Error('Error submitting review.');
      }

      // Close the popup and reload past cart events
      setShowReviewPopup(false);
      fetchData();
    } catch (error) {
      console.error('Error submitting review:', error);
    }
  };

  return (
    <div>
      <Nav />
      <div className="past-cart-events-page-main-container">
        <h2 className="h2teksti">Your Past Cart Events</h2>
        <div className="past-cart-events-container">
          {loading ? (
            <p>Loading...</p>
          ) : pastCartEvents.length > 0 ? (
            pastCartEvents.map((event) => (
              <div key={event.id} className="past-cart-event-item-container">
                {/* Display relevant information about the past cart event */}
                <p>Event Name: {event.event_name}</p>
                <p>Location: {event.location}</p>
                <p>Price: ${event.price}</p>
                <p>Event Date: {event.event_date}</p>
                {/* "Leave a Review" button */}
                <button onClick={() => handleLeaveReview(event)}>Leave a Review</button>
              </div>
            ))
          ) : (
            <p>No past cart events found.</p>
          )}
        </div>
      </div>

      {/* Review Popup */}
      {showReviewPopup && (
        <div className="review-popup">
          <h3>Leave a Review for {selectedEvent?.event_name}</h3>
          {/* Add input fields for rating and comment */}
          <label>
            Rating:
            <input
              type="number"
              min="1"
              max="5"
              value={rating}
              onChange={(e) => setRating(parseInt(e.target.value, 10))}
            />
          </label>
          <label>
            Comment:
            <textarea value={comment} onChange={(e) => setComment(e.target.value)} />
          </label>
          {/* Add a submit button */}
          <button onClick={handleReviewSubmit}>Submit Review</button>
          {/* Add a close button */}
          <button onClick={() => setShowReviewPopup(false)}>Close</button>
        </div>
      )}
    </div>
  );
};

export default PastCartEventsPage;
