import React, { useState, useEffect, useContext } from 'react';
import { UserContext } from './UserContext';
import { useNavigate } from 'react-router-dom';
import Nav from './Nav';

const PastCartEventsPage = () => {
  const { isAuthenticated, userData } = useContext(UserContext);
  const navigate = useNavigate();
  const [pastCartEvents, setPastCartEvents] = useState([]);
  const [loading, setLoading] = useState(true);

  // State for the review popup
  const [showReviewPopup, setShowReviewPopup] = useState(false);
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState('');

  // Define fetchData function outside useEffect
  const fetchData = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost/api/getPastCartEvents.php?userId=${userData.userId}`, {
        method: 'GET',
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        console.error(`HTTP error! Status: ${response.status}`);
        throw new Error('Error fetching past cart events.');
      }

      const data = await response.json();

      if (Array.isArray(data) && data.length > 0) {
        setPastCartEvents(data);
      } else {
        console.log('No past cart events found.');
      }

      setLoading(false);
    } catch (error) {
      console.error('Error fetching past cart events:', error);
      setLoading(false);
    }
  };

  useEffect(() => {
    // Call fetchData function inside useEffect
    fetchData();
  }, [navigate, isAuthenticated, userData]);

  // Function to handle the "Leave a Review" button click
  const handleLeaveReview = (event) => {
    setSelectedEvent(event);
    setShowReviewPopup(true);
  };

  // Function to handle the review submission
  const handleReviewSubmit = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost/api/submitReview.php', {
        method: 'POST',
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          userId: userData.userId,
          eventId: selectedEvent.id,
          rating,
          comment,
        }),
      });

      if (!response.ok) {
        console.error(`HTTP error! Status: ${response.status}`);
        throw new Error('Error submitting review.');
      }

      // Close the popup and reload past cart events
      setShowReviewPopup(false);
      fetchData();
    } catch (error) {
      console.error('Error submitting review:', error);
    }
  };

  return (
    <div className="past-cart-events-page bg-primary text-text-color">
      <Nav />

      <div className="past-cart-events-page-main-container max-w-screen-md mx-auto p-8">
        <h2 className="h2teksti text-third-bg">Your Past Cart Events</h2>
        <div className="past-cart-events-container grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
          {loading ? (
            <p className="loading-message text-text-color">Loading...</p>
          ) : pastCartEvents.length > 0 ? (
            pastCartEvents.map((event) => (
              <div key={event.id} className="past-cart-event-item-container bg-third-bg p-4 rounded">
                <p className="event-info text-text-color">Event Name: {event.event_name}</p>
                <p className="event-info text-text-color">Location: {event.location}</p>
                <p className="event-info text-text-color">Price: ${event.price}</p>
                <p className="event-info text-text-color">Event Date: {event.event_date}</p>
                <img src={event.image_link} alt={event.event_name} className="event-image w-full h-auto mt-4" />
                <button
                  className="review-button bg-button-1 text-third-bg px-4 py-2 mt-4 rounded cursor-pointer"
                  onClick={() => handleLeaveReview(event)}
                >
                  Leave a Review
                </button>
              </div>
            ))
          ) : (
            <p className="no-events-message text-text-color">No past cart events found.</p>
          )}
        </div>
      </div>

      {showReviewPopup && (
        <div className="review-popup fixed top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 bg-third-bg p-8 rounded shadow-md">
          <h3 className="text-text-color">Leave a Review for {selectedEvent?.event_name}</h3>
          <label className="review-label block mt-4">
            Rating:
            <input
              type="number"
              min="1"
              max="5"
              value={rating}
              onChange={(e) => setRating(parseInt(e.target.value, 10))}
              className="review-input w-full p-2 mt-1 border border-gray-300 rounded"
            />
          </label>
          <label className="review-label block mt-4">
            Comment:
            <textarea
              value={comment}
              onChange={(e) => setComment(e.target.value)}
              className="review-textarea w-full p-2 mt-1 border border-gray-300 rounded"
            />
          </label>
          <button
            className="submit-review-button bg-button-2 text-third-bg px-4 py-2 mt-4 mr-4 rounded cursor-pointer"
            onClick={handleReviewSubmit}
          >
            Submit Review
          </button>
          <button
            className="close-button bg-red-500 text-third-bg px-4 py-2 mt-4 rounded cursor-pointer"
            onClick={() => setShowReviewPopup(false)}
          >
            Close
          </button>
        </div>
      )}
    </div>
  );
};

export default PastCartEventsPage;
